// SPDX-License-Identifier: GPL-3.0
pragma solidity ^0.8.16;

import "../TransferHelper.sol";
import "../ImmutableOwnable.sol";

import "../../staking/interfaces/IFxStateSender.sol";
import "../../staking/interfaces/IFxMessageProcessor.sol";

interface IPZkp {
    function deposit(address user, bytes calldata depositData) external;
}

interface IRootChainManager {
    function depositFor(
        address receiver,
        address token,
        bytes calldata depositData
    ) external;
}

contract MockFxPortal is ImmutableOwnable, IRootChainManager, IFxStateSender {
    using TransferHelper for address;

    uint256[50] private __gap;

    // solhint-disable var-name-mixedcase
    address public immutable PZKP_TOKEN;
    address public immutable ZKP_TOKEN;

    // solhint-enable var-name-mixedcase

    event DepositForLog(address receiver, address token, bytes depositData);
    event SendMessageToChildLog(address _receiver, bytes _data);
    event ProcessMessageFromRootLog(
        uint256 stateId,
        address rootMessageSender,
        bytes data
    );

    constructor(
        address _owner,
        address _zkpToken,
        address _pZkpToken
    ) ImmutableOwnable(_owner) {
        require(
            _zkpToken != address(0) && _pZkpToken != address(0),
            "init: zero address"
        );

        ZKP_TOKEN = _zkpToken;
        PZKP_TOKEN = _pZkpToken;
    }

    // simulate message bridging
    function sendMessageToChild(address receiver, bytes calldata data)
        external
    {
        IFxMessageProcessor(receiver).processMessageFromRoot(
            uint256(0), // stateId
            msg.sender, // rootMessageSender
            data // content
        );

        emit SendMessageToChildLog(receiver, data);
    }

    // simulate token bridging
    function depositFor(
        address receiver,
        address token,
        bytes calldata depositData
    ) external {
        require(token == ZKP_TOKEN, "MOCKFX::depositFor: invalid token");

        uint256 amount = abi.decode(depositData, (uint256));
        require(amount > 0, "MOCKFX::depositFor: zero amount");

        token.safeTransferFrom(msg.sender, address(this), amount);
        IPZkp(PZKP_TOKEN).deposit(receiver, depositData);

        emit DepositForLog(receiver, token, depositData);
    }
}

contracts/common/NonReentrant.sol.content:
// SPDX-License-Identifier: MIT
// SPDX-FileCopyrightText: Copyright 2021-22 Panther Ventures Limited Gibraltar
// slither-disable-next-line solc-version
pragma solidity ^0.8.4;

/**
 * @title NonReentrant
 * @notice It provides reentrancy guard.
 * The code borrowed from openzeppelin-contracts.
 * Unlike original, this version requires neither `constructor` no `init` call.
 */
abstract contract NonReentrant {
    uint256 private constant _NOT_ENTERED = 1;
    uint256 private constant _ENTERED = 2;

    uint256 private _reentrancyStatus;

    modifier nonReentrant() {
        // Being called right after deployment, when _reentrancyStatus is 0 ,
        // it does not revert (which is expected behaviour)
        require(_reentrancyStatus != _ENTERED, "claimErc20: reentrant call");

        // Any calls to nonReentrant after this point will fail
        _reentrancyStatus = _ENTERED;

        _;

        // By storing the original value once again, a refund is triggered (see
        // https://eips.ethereum.org/EIPS/eip-2200)
        _reentrancyStatus = _NOT_ENTERED;
    }
}

contracts/common/proxy/EIP173Proxy.sol.content:
// SPDX-License-Identifier: MIT
// slither-disable-next-line solc-version
pragma solidity ^0.8.4;

import "./Proxy.sol";

interface ERC165 {
    function supportsInterface(bytes4 id) external view returns (bool);
}

///@notice Proxy implementing EIP173 for ownership management
contract EIP173Proxy is Proxy {
    // ////////////////////////// EVENTS ///////////////////////////////////////////////////////////////////////

    event OwnershipTransferred(
        address indexed previousOwner,
        address indexed newOwner
    );

    // /////////////////////// CONSTRUCTOR //////////////////////////////////////////////////////////////////////

    constructor(
        address implementationAddress,
        address ownerAddress,
        bytes memory data
    ) payable {
        _setImplementation(implementationAddress, data);
        _setOwner(ownerAddress);
    }

    // ///////////////////// EXTERNAL ///////////////////////////////////////////////////////////////////////////

    function owner() external view returns (address) {
        return _owner();
    }

    function supportsInterface(bytes4 id) external view returns (bool) {
        if (id == 0x01ffc9a7 || id == 0x7f5828d0) {
            return true;
        }
        if (id == 0xFFFFFFFF) {
            return false;
        }

        ERC165 implementation;
        // solhint-disable-next-line no-inline-assembly
        assembly {
            implementation := sload(
                0x360894a13ba1a3210667c828492db98dca3e2076cc3735a920a3ca505d382bbc
            )
        }

        // Technically this is not standard compliant as ERC-165 require 30,000 gas which that call cannot ensure
        // because it is itself inside `supportsInterface` that might only get 30,000 gas.
        // In practise this is unlikely to be an issue.
        try implementation.supportsInterface(id) returns (bool support) {
            return support;
        } catch {
            return false;
        }
    }

    function transferOwnership(address newOwner) external onlyOwner {
        _setOwner(newOwner);
    }

    function upgradeTo(address newImplementation) external onlyOwner {
        _setImplementation(newImplementation, "");
    }

    function upgradeToAndCall(address newImplementation, bytes calldata data)
        external
        payable
        onlyOwner
    {
        _setImplementation(newImplementation, data);
    }

    // /////////////////////// MODIFIERS ////////////////////////////////////////////////////////////////////////

    modifier onlyOwner() {
        require(msg.sender == _owner(), "NOT_AUTHORIZED");
        _;
    }

    // ///////////////////////// INTERNAL //////////////////////////////////////////////////////////////////////

    function _owner() internal view returns (address adminAddress) {
        // solhint-disable-next-line no-inline-assembly
        assembly {
            adminAddress := sload(
                0xb53127684a568b3173ae13b9f8a6016e243e63b6e8ee1178d6a717850b5d6103
            )
        }
    }

    function _setOwner(address newOwner) internal {
        address previousOwner = _owner();
        // solhint-disable-next-line no-inline-assembly
        assembly {
            sstore(
                0xb53127684a568b3173ae13b9f8a6016e243e63b6e8ee1178d6a717850b5d6103,
                newOwner
            )
        }
        emit OwnershipTransferred(previousOwner, newOwner);
    }
}

