        uint256 pubSpendingKeyY,
        uint64 scaledAmount,
        uint160 zAssetId,
        uint32 creationTime
    ) external pure returns (uint256) {
        return
            uint256(
                generateCommitment(
                    pubSpendingKeyX,
                    pubSpendingKeyY,
                    scaledAmount,
                    zAssetId,
                    creationTime
                )
            );
    }
}

