            z.tokenType = ERC20_TOKEN_TYPE;
            z.version = 0;
            z.scale = 0;
            z.token = address(Tokens[i]);
            z.status = zASSET_ENABLED;
            ZAssetsRegistryV0(registry).addZAsset(z);
        }
        exitTime = safe32TimeNow() + 1;
    }

