// SPDX-License-Identifier: BUSL-1.1
// SPDX-FileCopyrightText: Copyright 2023 Panther Ventures Limited Gibraltar
pragma solidity 0.8.16;

import "../common/ImmutableOwnable.sol";
import "../common/TransferHelper.sol";
import { HUNDRED_PERCENT } from "../common/Constants.sol";

contract OnboardingController is ImmutableOwnable {
    using TransferHelper for address;

    // solhint-disable var-name-mixedcase

    uint256 private constant ZERO_REWARD = 0;

    address public immutable ZACCOUNT_REGISTRY;
    address public immutable ZKP_TOKEN;
    address public immutable VAULT;
    address public immutable RESERVE_CONTROLLER;

    // solhint-enable var-name-mixedcase

    struct RewardParams {
        // reserved bytes
        uint128 _unused;
        // kyc provider ratio from zkpRewardsPerActivation
        uint16 zkpRate;
        // user ratio from zkpRewardsPerActivation
        uint16 zZkpRate;
        // zkp reward to be grant on each call.
        uint96 rewardsPerGrant;
    }

    RewardParams public rewardParams;

    uint128 public rewardsGranted;
    uint128 public rewardsLimit;

    mapping(address => bool) public isUserRewarded;

    event RewardParamsUpdated(
        uint96 rewardsPerGrant,
        uint16 zkpRate,
        uint16 zZkpRate
    );
    event RewardsLimtUpdated(uint256 rewardsLimit);
    event ZzkpAllocated(address user, uint256 amount);
    event ReserveControllerApproved(uint256 amount);

    constructor(
        address _owner,
        address _zkpToken,
        address _zAccountRegistry,
        address _vault,
        address _reserveController
    ) ImmutableOwnable(_owner) {
        require(
            _zAccountRegistry != address(0) &&
                _zkpToken != address(0) &&
                _vault != address(0),
            "init: zero address"
        );

        ZACCOUNT_REGISTRY = _zAccountRegistry;
        ZKP_TOKEN = _zkpToken;
        VAULT = _vault;
        RESERVE_CONTROLLER = _reserveController;
    }

    function updateRewardParams(
        uint96 _rewardsPerGrant,
        uint16 _zkpRate,
        uint16 _zZkpRate
    ) external onlyOwner {
        // if _rewardsPerGrant is defined, then sum of ratios should be 10000
        // setting _rewardsPerGrant to 0 disables the program
        require(
            _rewardsPerGrant == 0 || (_zkpRate + _zZkpRate == HUNDRED_PERCENT),
            "invalid zkp ratio"
        );

        rewardParams = RewardParams({
            _unused: uint128(0),
            zkpRate: _zkpRate,
            zZkpRate: _zZkpRate,
            rewardsPerGrant: _rewardsPerGrant
        });

        emit RewardParamsUpdated(_rewardsPerGrant, _zkpRate, _zZkpRate);
    }

    function updateRewardsLimitAndVaultAllowance() external {
        // Getting the current allowance of ReserveController
        uint256 reserveControllerAllowance = ZKP_TOKEN.safeAllowance(
            address(this),
            RESERVE_CONTROLLER
        );
        uint256 _rewardsLimit = rewardsLimit;

        // Getting the unused rewards limit
        uint256 unusedLimit = _rewardsLimit - rewardsGranted;

        // The availabe balance (part of the balance is reserved and will be withdrawn from ReserveController)
        uint256 available = ZKP_TOKEN.safeBalanceOf(address(this)) -
            reserveControllerAllowance;

        if (available == unusedLimit) return;

        if (available > unusedLimit) {
            uint256 newAllocation = available - unusedLimit;

            _rewardsLimit += uint128(newAllocation);

            // Approve the vault to transfer its zZkp portion
            ZKP_TOKEN.safeIncreaseAllowance(VAULT, newAllocation);
        } else {
            // gracefully handle this unexpected situation
            uint256 shortage = unusedLimit - available;

            _rewardsLimit = _rewardsLimit > shortage
                ? _rewardsLimit - shortage
                : 0;
        }

        rewardsLimit = uint128(_rewardsLimit);

        emit RewardsLimtUpdated(_rewardsLimit);
    }

    // TODO: review/update OnboardingController.grantRewards
    // solhint-disable no-unused-vars
    function grantRewards(
        address _user,
        uint8 _prevStatus,
        uint8 _newStatus,
        bytes memory _data
    ) external returns (uint256 _zZkpRewardAlloc) {
        _zZkpRewardAlloc = 100e18;

        // require(msg.sender == ZACCOUNT_REGISTRY, "unauthorized");

        // RewardParams memory _rewardParams = rewardParams;

        // uint256 _rewardsGranted = rewardsGranted +
        //     (_rewardParams.rewardsPerGrant);

        // if (rewardsLimit >= _rewardsGranted) {
        //     _zZkpRewardAlloc = _getZzkpRewardsAllocation(_rewardParams, _user);

        //     _increaseReserveControllerAllowance(_rewardParams);

        //     rewardsGranted = uint128(_rewardsGranted);
        // }
    }

    // solhint-enable no-unused-vars

    function _getZzkpRewardsAllocation(
        RewardParams memory _rewardParams,
        address _user
    ) private returns (uint256 _zZkpRewardAlloc) {
        // return 0 if has already got rewarded
        if (isUserRewarded[_user]) return (_zZkpRewardAlloc);

        // Calculate ZKP rewards allocation
        _zZkpRewardAlloc =
            ((_rewardParams.rewardsPerGrant) * _rewardParams.zZkpRate) /
            HUNDRED_PERCENT;

        if (_zZkpRewardAlloc > ZERO_REWARD) {
            isUserRewarded[_user] = true;

            emit ZzkpAllocated(_user, _zZkpRewardAlloc);
        }
    }

    function _increaseReserveControllerAllowance(
        RewardParams memory _rewardParams
    ) private {
        uint256 _zkpRewardAlloc = ((_rewardParams.rewardsPerGrant) *
            _rewardParams.zkpRate) / HUNDRED_PERCENT;

        if (_zkpRewardAlloc > ZERO_REWARD) {
            ZKP_TOKEN.safeIncreaseAllowance(
                RESERVE_CONTROLLER,
                _zkpRewardAlloc
            );

            emit ReserveControllerApproved(_zkpRewardAlloc);
        }
    }

    // The contract is supposed to run behind a proxy DELEGATECALLing it.
    // On upgrades, adjust `__gap` to match changes of the storage layout.
    // slither-disable-next-line shadowing-state unused-state
    uint256[50] private __gap;
}

contracts/protocol/PantherBusTree.sol.content:
// SPDX-License-Identifier: BUSL-1.1
// SPDX-FileCopyrightText: Copyright 2023 Panther Ventures Limited Gibraltar
pragma solidity ^0.8.16;

import "./interfaces/IPantherPoolV1.sol";

import "./pantherForest/busTree/BusTree.sol";
import { FIELD_SIZE } from "./crypto/SnarkConstants.sol";
import { ERC20_TOKEN_TYPE } from "../common/Constants.sol";
import { LockData } from "../common/Types.sol";
import "../common/ImmutableOwnable.sol";
import "./crypto/PoseidonHashers.sol";
import "./errMsgs/PantherBusTreeErrMsgs.sol";

contract PantherBusTree is BusTree, ImmutableOwnable {
    // The contract is supposed to run behind a proxy DELEGATECALLing it.
    // On upgrades, adjust `__gap` to match changes of the storage layout.
    // slither-disable-next-line shadowing-state unused-state
    uint256[50] private __gap;

    // solhint-disable var-name-mixedcase

    // timestamp of deployment
    uint256 public immutable START_TIME;

    // address of reward token
    address public immutable REWARD_TOKEN;

    // solhint-enable var-name-mixedcase

    // avg number of utxos which can be added per minute
    uint16 public perMinuteUtxosLimit;

    // base reward per each utxo
    uint96 public basePerUtxoReward;

    // keeps track of number of the added utxos
    uint32 public utxoCounter;

    event MinerRewarded(address miner, uint256 reward);

    constructor(
        address owner,
        address rewardToken,
        address _pantherPool,
        address _verifier,
        uint160 _circuitId
    ) ImmutableOwnable(owner) BusTree(_verifier, _circuitId, _pantherPool) {
        require(rewardToken != address(0), ERR_INIT);

        START_TIME = block.timestamp;

        REWARD_TOKEN = rewardToken;
    }

    // TODO: Remove _perMinuteUtxosLimit after Testnet (required for Stage #0..2 only)
    function updateParams(
        uint16 _perMinuteUtxosLimit,
        uint96 _basePerUtxoReward,
        uint16 reservationRate,
        uint16 premiumRate,
        uint16 minEmptyQueueAge
    ) external onlyOwner {
        BusQueues.updateParams(reservationRate, premiumRate, minEmptyQueueAge);

        require(
            _perMinuteUtxosLimit > 0 && _basePerUtxoReward > 0,
            ERR_ZERO_REWARD_PARAMS
        );
        perMinuteUtxosLimit = _perMinuteUtxosLimit;
        basePerUtxoReward = _basePerUtxoReward;
    }

    function rewardMiner(address miner, uint256 reward) internal override {
        LockData memory data = LockData({
            tokenType: ERC20_TOKEN_TYPE,
            token: REWARD_TOKEN,
            tokenId: 0,
            extAccount: miner,
            extAmount: uint96(reward)
        });

        IPantherPoolV1(PANTHER_POOL).unlockAssetFromVault(data);

        emit MinerRewarded(miner, reward);
    }

    function hash(bytes32 left, bytes32 right)
        internal
        pure
        override
        returns (bytes32)
    {
        return PoseidonHashers.poseidonT3([left, right]);
    }

    function getAllowedUtxosAt(uint256 _timestamp, uint256 _utxoCounter)
        public
        view
        returns (uint256 allowedUtxos)
    {
        if (_timestamp < START_TIME) return 0;

        uint256 secs = _timestamp - START_TIME;
        allowedUtxos = (secs * perMinuteUtxosLimit) / 60 seconds - _utxoCounter;
    }

    function addUtxoToBusQueue(bytes32 utxo)
        external
        returns (uint32 queueId, uint8 indexInQueue)
    {
        require(msg.sender == PANTHER_POOL, ERR_UNAUTHORIZED);

        bytes32[] memory utxos = new bytes32[](1);
        utxos[0] = utxo;

        queueId = _nextQueueId == 0 ? 0 : _nextQueueId - 1;
        BusQueue memory busQueue = _busQueues[queueId];
        indexInQueue = busQueue.nUtxos;

        addUtxosToBusQueue(utxos, uint96(basePerUtxoReward));
    }

    // TODO: Remove simulateAddUtxosToBusQueue after Testnet (required for Stage #0..2 only)
    function simulateAddUtxosToBusQueue() external {
        uint256 _counter = uint256(utxoCounter);

        // generating the first utxo
        uint256 utxo = uint256(keccak256(abi.encode(_counter))) % FIELD_SIZE;

        // Generating the utxos length between 1 - 5
        uint256 length = (utxo & 3) + 1;

        if (_counter + length > getAllowedUtxosAt(block.timestamp, _counter))
            return;

        bytes32[] memory utxos = new bytes32[](length);

        // adding the first commitment
        utxos[0] = bytes32(utxo);
        _counter++;

        // adding the rest of commitment
        for (uint256 i = 1; i < length; ) {
            utxos[i] = bytes32(
                uint256(keccak256(abi.encode(_counter))) % FIELD_SIZE
            );

            unchecked {
                i++;
                _counter++;
            }
        }

        // overflow risk ignored
        utxoCounter = uint32(_counter);
        uint256 reward = uint256(basePerUtxoReward) * length;

        addUtxosToBusQueue(utxos, uint96(reward));
    }
}

