// SPDX-License-Identifier: BUSL-1.1
// SPDX-FileCopyrightText: Copyright 2021-22 Panther Ventures Limited Gibraltar
pragma solidity ^0.8.16;

import { PoseidonT3, PoseidonT4 } from "../crypto/Poseidon.sol";
import "../errMsgs/PantherPoolErrMsgs.sol";
import "../triadTree/TriadIncrementalMerkleTrees.sol";

abstract contract MerkleProofVerifier {
    // @dev Number of levels in a tree excluding the root level
    // (also defined in scripts/generateTriadMerkleZeroesContracts.sh)
    uint256 private constant TREE_DEPTH = 15;

    //t |bH  bL| Subtree
    //--|------|------------
    //0 | 0  0 | hash(C,L,R)
    //1 | 0  1 | hash(L,C,R)
    //2 | 1  0 | hash(L,R,C)
    //3 | 1  1 | Not allowed
    //--|------|------------
    // Current leaf index in triad is (C,L,R)
    uint256 private constant iTRIAD_INDEX_LEFT = 0x0;
    // Current leaf index in triad is (L,C,R)
    uint256 private constant iTRIAD_INDEX_MIDDLE = 0x1;
    // Current leaf index in triad is (L,R,C)
    uint256 private constant iTRIAD_INDEX_RIGHT = 0x2;
    // Forbidden triad value in tria is `11`
    uint256 private constant iTRIAD_INDEX_FORBIDDEN = 0x3;

    /// @param merkleRoot - verify checked to this hash
    /// @param triadIndex - index inside triad = { 0, 1, 2 }
    /// @param triadNodeIndex - index of triad hash ( c0,c1,c2 ) in the tree - Triad contract insures its is in range
    /// @param leaf - commitment leaf value
    /// @param pathElements - TREE_DEPTH + 1 elements - c1,c2 & path-elements
    /// @dev Returns true if a `leaf` can be proved to be a part of a Merkle tree
    /// @dev defined by `root`. For this, a `proof` must be provided, containing
    /// @dev sibling hashes on the branch from the leaf to the root of the tree.
    function verifyMerkleProof(
        bytes32 merkleRoot,
        uint256 triadIndex,
        uint256 triadNodeIndex,
        bytes32 leaf,
        bytes32[TREE_DEPTH + 1] calldata pathElements
    ) internal pure {
        // [0] - Assumed it is computed by the TriadIncrementalMerkleTrees
        //       using modulo operation, so no need to check lower range
        //require(iTRIAD_INDEX_LEFT <= triadIndex, ERR_TRIAD_INDEX_MIN_VALUE);
        require(triadIndex < iTRIAD_INDEX_FORBIDDEN, ERR_TRIAD_INDEX_MAX_VALUE);

        // [1] - Compute zero level hash
        // variable will be initialized inside the 'if' bellow
        // slither-disable-next-line uninitialized-local
        bytes32 nodeHash;
        // NOTE: no else-case needed since this code executed after require at step [0]
        if (triadIndex == iTRIAD_INDEX_LEFT) {
            nodeHash = PoseidonT4.poseidon(
                [leaf, pathElements[0], pathElements[1]]
            );
        } else if (triadIndex == iTRIAD_INDEX_MIDDLE) {
            nodeHash = PoseidonT4.poseidon(
                [pathElements[0], leaf, pathElements[1]]
            );
        } else if (triadIndex == iTRIAD_INDEX_RIGHT) {
            nodeHash = PoseidonT4.poseidon(
                [pathElements[0], pathElements[1], leaf]
            );
        }

        // [2] - Compute root
        for (uint256 level = 2; level < pathElements.length; level++) {
            bool isLeftNode;
            unchecked {
                // triadNodeIndex is actually a path to triad-node in merkle-tree
                // each LSB bit of this number is left or right path
                // it means for example: path = b111 , zero leaf will be from right size of hash
                // and path element[2] will be from right side of hash, all other path elements [3,4] will be from
                // left side of the next hashes till root.
                isLeftNode = ((triadNodeIndex & (0x1 << (level - 2))) == 0);
            }
            if (isLeftNode) {
                // computed node from left side
                // Hash(left = nodeHash, right = pathElement)
                nodeHash = PoseidonT3.poseidon([nodeHash, pathElements[level]]);
            } else {
                // computed node from right side
                // Hash(left = pathElement, right = nodeHash)
                nodeHash = PoseidonT3.poseidon([pathElements[level], nodeHash]);
            }
        }
        // [3] - revert if verification fails
        require(merkleRoot == nodeHash, ERR_MERKLE_PROOF_VERIFICATION_FAILED);
    }
}

contracts/protocol/pantherPool/Nullifier.content:
// SPDX-License-Identifier: BUSL-1.1
// SPDX-FileCopyrightText: Copyright 2021-22 Panther Ventures Limited Gibraltar
pragma solidity ^0.8.16;

import { PoseidonT3 } from "../crypto/Poseidon.sol";
import { FIELD_SIZE } from "../crypto/SnarkConstants.sol";
import { ERR_TOO_LARGE_LEAFID, ERR_TOO_LARGE_PRIVKEY } from "../errMsgs/PantherPoolErrMsgs.sol";

abstract contract NullifierGenerator {
    function generateNullifier(uint256 privSpendingKey, uint256 leafId)
        internal
        pure
        returns (bytes32 nullifier)
    {
        require(privSpendingKey < FIELD_SIZE, ERR_TOO_LARGE_PRIVKEY);
        require(leafId < FIELD_SIZE, ERR_TOO_LARGE_LEAFID);
        nullifier = PoseidonT3.poseidon(
            [bytes32(privSpendingKey), bytes32(leafId)]
        );
    }
}

contracts/protocol/pantherPool/PubKeyGen.content:
// SPDX-License-Identifier: BUSL-1.1
// SPDX-FileCopyrightText: Copyright 2021-22 Panther Ventures Limited Gibraltar
pragma solidity ^0.8.16;

import { G1Point } from "../../common/Types.sol";
import { ERR_TOO_LARGE_PRIVKEY } from "../errMsgs/PantherPoolErrMsgs.sol";
import { FIELD_SIZE } from "../crypto/SnarkConstants.sol";
import "../crypto/BabyJubJub.sol";

abstract contract PubKeyGenerator {
    function generatePubSpendingKey(uint256 privKey)
        internal
        view
        returns (G1Point memory pubKey)
    {
        // [0] - Require
        require(privKey < FIELD_SIZE, ERR_TOO_LARGE_PRIVKEY);
        // [1] - Generate public key
        G1Point memory base8 = G1Point({
            x: BabyJubJub.BASE8_X,
            y: BabyJubJub.BASE8_Y
        });
        pubKey = BabyJubJub.mulPointEscalar(base8, privKey);
    }
}

contracts/protocol/pantherPool/Transacti.content:
// SPDX-License-Identifier: BUSL-1.1
// SPDX-FileCopyrightText: Copyright 2021-22 Panther Ventures Limited Gibraltar
pragma solidity ^0.8.16;

/***
 * @dev Every MASP transaction is accompanied by the "Transaction Note" - data
 * needed to process the transaction (think of opening UTXOs), reconstruct user
 * operations history (think of "wallet cold start"), or send a private message
 * to a user in extraordinary cases (think of a "subpoena").
 * `PantherPool` smart contract publishes Transaction Notes as "events" (logs).
 * The Transaction Note contains one or a few "messages", which may be "public"
 * or "private".
 * Public messages contain publicly seen on-chain data. Smart contracts, rather
 * than users (the DApp), compose public messages.
 * As the name assumes, private messages contain private data, and user (DApp)
 * prepares and encrypts these messages to pass them to smart contracts.
 * Every private message is encrypted with the reading key of a receiver, who
 * may be a recipient of an UTXO, or the user that spends UTXOs ("messages to
 * the future"), or even a user not involved in spending/creating UTXOs.
 * Smart contracts don't parse private messages but rather copy private messages
 * "as is" into Transaction Notes.
 * Every message belongs to a certain "message type". The message type defines
 * the exact message length and the content.
 * For "fixed-content" messages, the message type defines all data fields - i.e.
 * data interpretation, formats/size, and the sequence the fields must follow in.
 * Data fields of the "free-content" are unspecified, unlike the content size.
 * Public messages always have fixed content. Private messages may have fixed or
 * free content.
 * For fixed-content private messages, the protocol also specifies the content
 * of the preimage and the cipher to apply.
 * Every MASP transaction belongs to one of a few "transaction types".
 * For every transaction type, the protocol specifies "mandatory" messages which
 * MUST be included in the Transaction Note.
 * Users (DApp) may append "optional" messages to mandatory messages.
 * There is also a special "void" message that has no content. It MAY replace a
 * mandatory message when data is missing/undefined/irrelevant, providing public
 * knowledge of this fact does not leak privacy.
 */
abstract contract TransactionNoteEmitter {
    // @notice Transaction Note, emitted with every MASP transaction
    event TransactionNote(uint8 txType, bytes content);

    // **** `bytes content`

    /* START of pseudo-code
    bytes content = abi.packed(messages[0], ..., messages[numMessages - 1]);
    numMessages = for_txType_num_of_mandatory_mssgs + num_of_opt_mssgs;

    // For "void" message type:
    bytes messages[i] = abi.packed(byte msgType);

    // For messages of types other than "void":
    bytes messages[i] = abi.packed(
        byte msgType,
        bytes[for_msgType_length] msgContainer
    );

    // Public fixed-content messages:
    // Smart contract MUST compose `msgContainer` as defined by `msgType`
    // (no `ephemeralKey` needed as data is publicly seen)
    bytes msgContainer = avi.packed(<abi.packed on-chain data>)

    // Private fixed-content messages:
    // DApp MUST compose the `ciphertext` as defined by the `msgType`
    bytes msgContainer = avi.packed(
        bytes32(ephemeralKey),
        bytes[for_msgType_ciphertext_length] ciphertext
    )

    // Private free-data messages:
    // nBlocks - number of 16-byte blocks the `msgContent` occupies
    require(nBlocks >= 1 && nBlocks =< 16)
    msgType = 0x2F + nBlocks;
    // DApp is not limited in composing (structuring) `msgContainer`
    bytes messages[i] = abi.packed(
        byte msgType,
        bytes32(ephemeralKey),
        bytes[nBlocks*16] msgContainer
    )
    END of pseudo-code */

    // **** Transaction Types

    // The range for `uint8 txType` divided into sub-ranges:
    //  - 0x00 .. 0x1F allowed
    //  - 0x20 .. 0xFF reserved (unused)

    // solhint-disable var-name-mixedcase

    uint8 internal constant TT_ZACCOUNT_ACTIVATION = 0x01;
    // TransactionNote for this tx type MUST include in the specified sequence:
    // - MT_UTXO_CREATE_TIME
    // - MT_UTXO_BUSTREE_IDS
    // - MT_UTXO_ZACCOUNT

    // **** Message Types

    // The range for `uint8 msgType` divided into sub-ranges:
    //  - 0x00 - the "void" (empty) message
    //  - 0x01 .. 0x2F for fixed-content private messages
    //  - 0x30 .. 0x3F for free-content private messages
    //  - 0x40 .. 0x5F reserved (unused)
    //  - 0x60 .. 0x7F for fixed-content public messages
    //  - 0x80 .. 0xFF reserved (unused)

    // "Void" type messages contain just this single byte:
    uint8 internal constant MT_VOID = 0x00;
    // Length in bytes
    uint256 internal constant LMT_VOID = 1;

    // zAccount UTXO opening values:
    uint8 internal constant MT_UTXO_ZACCOUNT = 0x06;
    // `msgContainer` MUST include the following data:
    // - bytes[64] cypherText
    // Length in bytes (msgType, ephemeralKey, msgContainer)
    uint256 internal constant LMT_UTXO_ZACCOUNT = 1 + 32 + 64;
    // Preimage of `cipherText` MUST contain (512 bit):
    // - random (256 bit)
    // - networkId (6 bit)
    // - zoneId (16 bit)
    // - nonce (24 bit)
    // - expiryTime (32 bit)
    // - amountZkp (64 bit)
    // - amountPrp (50 bit)
    // - totalAmountPerTimePeriod (64 bit)

    // Creation time of UTXO:
    uint8 internal constant MT_UTXO_CREATE_TIME = 0x60;
    // `msgContainer` MUST include the following data:
    // - uint32 creationTime
    // Length in bytes (msgType, msgContainer)
    uint256 internal constant LMT_UTXO_CREATE_TIME = 1 + 4;

    uint8 internal constant MT_UTXO_SPEND_TIME = 0x61;
    // `msgContainer` MUST include the following data:
    // - uint32 spendType
    // Length in bytes (msgType, msgContainer)
    uint256 internal constant LMT_UTXO_SPEND_TIME = 1 + 4;

    uint8 internal constant MT_UTXO_BUSTREE_IDS = 0x62;
    // `msgContainer` MUST include the following data:
    // - bytes32 commitment
    // - uint32 queueId
    // - uint8 indexInQueue
    // Length in bytes (msgType, msgContainer)
    uint256 internal constant LMT_UTXO_BUSTREE_IDS = 1 + 37;

    // solhint-enable var-name-mixedcase
}

contracts/protocol/PantherPoolV0.sol.content:
// SPDX-License-Identifier: BUSL-1.1
// SPDX-FileCopyrightText: Copyright 2021-22 Panther Ventures Limited Gibraltar
pragma solidity 0.8.16;

import "../common/Constants.sol";
import "./errMsgs/PantherPoolErrMsgs.sol";
import "../common/ImmutableOwnable.sol";
import "../common/NonReentrant.sol";
import "../common/Types.sol";
import "../common/Utils.sol";
import "./interfaces/IPrpGrantor.sol";
import "./interfaces/IVault.sol";
import "./interfaces/IZAssetsRegistry.sol";
import "../common/Claimable.sol";
import "./pantherPool/AmountConvertor.sol";
import "./pantherPool/CommitmentGenerator.sol";
import "./pantherPool/CommitmentsTrees.sol";
import "./pantherPool/MerkleProofVerifier.sol";
import "./pantherPool/NullifierGenerator.sol";
import "./pantherPool/PubKeyGenerator.sol";

/**
 * @title PantherPool
 * @author Pantherprotocol Contributors
 * @notice Multi-Asset Shielded Pool main contract v0
 * @dev It is the "version 0" of the Panther Protocol Multi-Asset Shielded Pool ("MASP").
 * It locks assets (ERC-20, ERC-721 or ERC-1155 tokens) of a user with the `Vault` smart
 * contract and generates UTXO's in the MASP for the user (i.e. builds merkle trees of
 * UTXO's commitments).
 * This contract does not implement the functionality for spending UTXO's (other than the
 * `exit` described further) and is supposed to be upgraded with the new one.
 * The new contract, the "v.1" of the MASP, is planned to implement spending of UTXO's
 * using zero-knowledge proves.
 * To be upgradable, this contract is assumed to run as an "implementation" for a proxy
 * that DELEGATECALL's the implementation.
 * To protect holders against lost of assets in case this contract is not upgraded, it
 * exposes the `exit` function, through which users may withdraw their locked assets via
 * revealing preimages of commitments.
 */
contract PantherPoolV0 is
    ImmutableOwnable,
    NonReentrant,
    Claimable,
    CommitmentsTrees,
    AmountConvertor,
    CommitmentGenerator,
    MerkleProofVerifier,
    NullifierGenerator,
    PubKeyGenerator,
    Utils
{
    // The contract is supposed to run behind a proxy DELEGATECALLing it.
    // On upgrades, adjust `__gap` to match changes of the storage layout.
    // slither-disable-next-line shadowing-state unused-state
    uint256[50] private __gap;

    // solhint-disable var-name-mixedcase

    /// @notice Address of the ZAssetRegistry contract
    address public immutable ASSET_REGISTRY;

    /// @notice Address of the Vault contract
    address public immutable VAULT;

    // solhint-enable var-name-mixedcase

    /// @notice (UNIX) Time since when the `exit` calls get enabled
    uint32 public exitTime;

    /// @notice Period (seconds) since `commitToExit` when `exit` opens
    // Needed to mitigate front-run attacks on `exit`
    uint24 public exitDelay;

    // (rest of the storage slot) reserved for upgrades
    // slither-disable-next-line unused-state,constable-states
    uint200 private _reserved;

    // @notice Seen (i.e. spent) commitment nullifiers
    // nullifier hash => spent
    mapping(bytes32 => bool) public isSpent;

    /// @notice Unused registered commitments to exit
    // hash(privSpendKey, recipient) => commitment timestamp
    mapping(bytes32 => uint32) public exitCommitments;

    /// @dev Emitted when exit time and/or exit delay updated
    event ExitTimesUpdated(uint256 newExitTime, uint256 newExitDelay);

    /// @dev New nullifier has been seen
    event Nullifier(bytes32 nullifier);

    /// @dev A tiny disowned token amount gets locked in the Vault
    /// (as a result of imprecise scaling of deposited amounts)
    event Change(address indexed token, uint256 change);

    /// @dev New exit commitment registered
    event ExitCommitment(uint256 timestamp);

    /// @param _owner Address of the `OWNER` who may call `onlyOwner` methods
    /// @param assetRegistry Address of the ZAssetRegistry contract
    /// @param vault Address of the Vault contract
    constructor(
        address _owner,
        address assetRegistry,
        address vault
    ) ImmutableOwnable(_owner) {
        require(TRIAD_SIZE == OUT_UTXOs, "E0");

        revertZeroAddress(assetRegistry);
        revertZeroAddress(vault);

        // As it runs behind the DELEGATECALL'ing proxy, initialization of
        // immutable "vars" only is allowed in the constructor

        ASSET_REGISTRY = assetRegistry;
        VAULT = vault;
    }

    /// @notice Update the exit time and the exit delay
    /// @dev Owner only may calls
    function updateExitTimes(uint32 newExitTime, uint24 newExitDelay)
        external
        onlyOwner
    {
        require(
            newExitTime >= exitTime &&
                newExitTime < MAX_TIMESTAMP &&
                newExitDelay != 0,
            "E1"
        );

        exitTime = newExitTime;
        exitDelay = newExitDelay;

        emit ExitTimesUpdated(uint256(newExitTime), uint256(newExitDelay));
    }

    /// @notice Transfer assets from the msg.sender to the VAULT and generate UTXOs in the MASP
    /// @param tokens Address of the token contract for every UTXO
    /// @param tokenIds For ERC-721 and ERC-1155 - token ID or subId of the token, 0 for ERC-20
    /// @param amounts Token amounts (unscaled) to be deposited
    /// @param pubSpendingKeys Public Spending Key for every UTXO
    /// @param secrets Encrypted opening values for every UTXO
    /// @param createdAt Optional, if 0 the network time used
    /// @dev createdAt must be less (or equal) the network time
    /// @return leftLeafId The `leafId` of the first UTXO (leaf) in the batch
    function generateDeposits(
        address[OUT_MAX_UTXOs] calldata tokens,
        uint256[OUT_MAX_UTXOs] calldata tokenIds,
        uint256[OUT_MAX_UTXOs] calldata amounts,
        G1Point[OUT_MAX_UTXOs] calldata pubSpendingKeys,
        uint256[CIPHERTEXT1_WORDS][OUT_MAX_UTXOs] calldata secrets,
        uint32 createdAt
    ) external nonReentrant returns (uint256 leftLeafId) {
        require(exitTime > 0, ERR_UNCONFIGURED_EXIT_TIME);

        uint32 timestamp = safe32TimeNow();
        if (createdAt != 0) {
            // Time comparison is acceptable in this case since block time accuracy is enough for this scenario
            // slither-disable-next-line timestamp
            require(createdAt <= timestamp, ERR_TOO_EARLY_CREATED_AT);
            timestamp = createdAt;
        }

        // variables get initialized inside the loop bellow
        // slither-disable-next-line uninitialized-local
        bytes32[OUT_MAX_UTXOs] memory commitments;
        // slither-disable-next-line uninitialized-local
        bytes[OUT_MAX_UTXOs] memory perUtxoData;

        // Types of UTXO data messages packed into one byte
        uint8 msgTypes = uint8(0);

        for (uint256 utxoIndex = 0; utxoIndex < OUT_MAX_UTXOs; utxoIndex++) {
            // The next call can't trigger the "calls loop" since it triggers
            // external calls to known contracts, which are trusted to handle
            // reentrancy risk properly.
            // Slither's "disable calls-loop detector" directives are inserted
            // in lines (bellow) with external calls rather than here only (as
            // otherwise slither reports false-positive issues).
            (uint160 zAssetId, uint64 scaledAmount) = _processDepositedAsset(
                tokens[utxoIndex],
                tokenIds[utxoIndex],
                amounts[utxoIndex]
            );

            if (utxoIndex != 0) msgTypes = msgTypes << 2;

            if (scaledAmount == 0) {
                // the zero UTXO
                // At least the 1st deposited amount shall be non-zero
                require(utxoIndex != 0, ERR_ZERO_DEPOSIT);

                commitments[utxoIndex] = ZERO_VALUE;
                perUtxoData[utxoIndex] = "";
                // As UTXO_DATA_TYPE5 is 0, next statement may be skipped
                // msgTypes |= UTXO_DATA_TYPE5;
            } else {
                // non-zero UTXO
                commitments[utxoIndex] = generateCommitment(
                    pubSpendingKeys[utxoIndex].x,
                    pubSpendingKeys[utxoIndex].y,
                    scaledAmount,
                    zAssetId,
                    timestamp
                );

                uint256 zAssetIdAndAmount = (uint256(zAssetId) << 96) |
                    uint256(scaledAmount);

                if (tokenIds[utxoIndex] != 0) {
                    msgTypes |= UTXO_DATA_TYPE1;
                    perUtxoData[utxoIndex] = abi.encodePacked(
                        secrets[utxoIndex],
                        zAssetIdAndAmount
                    );
                } else {
                    msgTypes |= UTXO_DATA_TYPE3;
                    perUtxoData[utxoIndex] = abi.encodePacked(
                        secrets[utxoIndex],
                        zAssetIdAndAmount,
                        tokenIds[utxoIndex]
                    );
                }
            }
        }

        leftLeafId = addAndEmitCommitments(
            commitments,
            msgTypes,
            perUtxoData,
            timestamp
        );
    }

    /// @notice Register future `exit` to protect against front-run and DoS.
    /// The `exit` is possible only after `exitDelay` since this function call.
    /// @param exitCommitment Commitment to the UTXO spending key and the recipient address.
    /// MUST be equal to keccak256(abi.encode(uint256(privSpendingKey), address(recipient)).
    function commitToExit(bytes32 exitCommitment) external {
        // Time comparison is acceptable in this case since block time accuracy is enough for this scenario
        // slither-disable-next-line incorrect-equality,timestamp
        require(
            exitCommitments[exitCommitment] == uint32(0),
            ERR_EXITCOMMIT_EXISTS
        );
        uint32 timestamp = safe32TimeNow();
        exitCommitments[exitCommitment] = timestamp;
        emit ExitCommitment(timestamp);
    }

    /// @notice Spend an UTXO in the MASP and withdraw the asset from the Vault to the msg.sender.
    /// This function call must be registered in advance with `commitToExit`.
    /// @param token Address of the token contract
    /// @param subId '_tokenId'/'_id' for ERC-721/1155, 0 for the "default" zAsset of an ERC-20 token,
    // or `subId` for an "alternative" zAsset of an ERC-20 (see ZAssetRegistry.sol for details)
    /// @param scaledAmount Token scaled amount
    /// @param privSpendingKey UTXO's Private Spending Key
    /// @param leafId Id of the leaf with the UTXO commitments in the Merkle Trees
    /// @param pathElements Elements of the Merkle proof of inclusion
    /// @param merkleRoot The root of the Merkle Tree the leaf is a part of
    /// @param cacheIndexHint Index of the `merkleRoot` in the cache of roots, 0 by default
    /// @dev `cacheIndexHint` needed for the "current" (partially populated) tree only
    function exit(
        address token,
        uint256 subId,
        uint64 scaledAmount,
        uint32 creationTime,
        uint256 privSpendingKey,
        uint256 leafId,
        bytes32[TREE_DEPTH + 1] calldata pathElements,
        bytes32 merkleRoot,
        uint256 cacheIndexHint
    ) external nonReentrant {
        // if exitTime == 0 -> `exit` is not accepted since init phase is not finished yet
        // Time comparison is acceptable in this case since block time accuracy is enough for this scenario
        // slither-disable-next-line timestamp
        require(
            safe32TimeNow() >= exitTime && exitTime != 0,
            ERR_TOO_EARLY_EXIT
        );
        _verifyExitCommitment(privSpendingKey, msg.sender);

        {
            bytes32 nullifier = generateNullifier(privSpendingKey, leafId);
            require(!isSpent[nullifier], ERR_SPENT_NULLIFIER);
            isSpent[nullifier] = true;
            emit Nullifier(nullifier);
        }
        require(
            isKnownRoot(getTreeId(leafId), merkleRoot, cacheIndexHint),
            ERR_UNKNOWN_MERKLE_ROOT
        );

        ZAsset memory asset;
        uint256 _tokenId;
        {
            bytes32 commitment;
            {
                uint160 zAssetId;
                {
                    (zAssetId, _tokenId, , asset) = IZAssetsRegistry(
                        ASSET_REGISTRY
                    ).getZAssetAndIds(token, subId);
                    require(asset.status == zASSET_ENABLED, ERR_WRONG_ASSET);
                }
                G1Point memory pubSpendingKey = generatePubSpendingKey(
                    privSpendingKey
                );
                commitment = generateCommitment(
                    pubSpendingKey.x,
                    pubSpendingKey.y,
                    scaledAmount,
                    zAssetId,
                    creationTime
                );
            }
            verifyMerkleProof(
                merkleRoot,
                _getTriadIndex(leafId),
                _getTriadNodeIndex(leafId),
                commitment,
                pathElements
            );
        }

        uint96 amount = _unscaleAmount(scaledAmount, asset.scale);
        IVault(VAULT).unlockAsset(
            LockData(asset.tokenType, token, _tokenId, msg.sender, amount)
        );
    }

    /// @notice Withdraw accidentally sent tokens or ETH from this contract
    /// @dev The "owner" may call only
    function claimEthOrErc20(
        address token,
        address to,
        uint256 amount
    ) external onlyOwner nonReentrant {
        _claimEthOrErc20(token, to, amount);
    }

    /// Internal and private functions follow

    // Declared `internal` rather than `private` to ease testing
    function _processDepositedAsset(
        address token,
        uint256 subId,
        uint256 amount
    ) internal returns (uint160 zAssetId, uint64 scaledAmount) {
        // Do nothing if it's the "zero" (or "dummy") deposit
        if (amount == 0) {
            // Both token and subId must be zeros for the "zero" deposit
            require(token == address(0) && subId == 0, ERR_WRONG_DEPOSIT);
            return (0, 0);
        }
        // amount can't be zero here and further

        // At this point, a non-zero deposit of a real asset (token) expected
        uint256 _tokenId;
        ZAsset memory asset;

        // Note comments on "calls-loop" in `function generateDeposits`
        // slither-disable-next-line calls-loop
        (zAssetId, _tokenId, , asset) = IZAssetsRegistry(ASSET_REGISTRY)
            .getZAssetAndIds(token, subId);
        require(asset.status == zASSET_ENABLED, ERR_WRONG_ASSET);

        // Scale amount, if asset.scale provides for it (ERC-20 only)
        uint256 change;
        (scaledAmount, change) = _scaleAmount(amount, asset.scale);

        // The `change` will remain locked in the Vault until it's claimed
        // (when and if future upgrades implement change claiming)
        if (change > 0) emit Change(token, change);

        // slither-disable-next-line calls-loop
        IVault(VAULT).lockAsset(
            LockData(
                asset.tokenType,
                asset.token,
                _tokenId,
                msg.sender,
                uint96(amount)
            )
        );

        return (zAssetId, scaledAmount);
    }

    function _verifyExitCommitment(uint256 privSpendingKey, address recipient)
        internal
    {
        bytes32 commitment = keccak256(abi.encode(privSpendingKey, recipient));

        uint32 commitmentTime = exitCommitments[commitment];
        // Time comparison is acceptable in this case since block time accuracy is enough for this scenario
        // slither-disable-next-line timestamp
        require(commitmentTime != uint32(0), ERR_EXITCOMMIT_MISSING);

        uint256 allowedTime = uint256(commitmentTime) + uint256(exitDelay);
        // Time comparison is acceptable in this case since block time accuracy is enough for this scenario
        // slither-disable-next-line timestamp
        require(timeNow() > allowedTime, ERR_EXITCOMMIT_LOCKED);

        // Let's gain some gas back
        exitCommitments[commitment] = uint32(0);
        // No extra event emitted as spent UTXO and withdrawal events will fire
    }
}

contracts/protocol/PantherPoolV1.sol.content:
// SPDX-License-Identifier: BUSL-1.1
// SPDX-FileCopyrightText: Copyright 2023s Panther Ventures Limited Gibraltar
pragma solidity ^0.8.16;

import "./interfaces/IVault.sol";
import "./interfaces/IPantherVerifier.sol";
import "./interfaces/IBusTree.sol";
import "./../common/ImmutableOwnable.sol";
import { LockData } from "./../common/Types.sol";
import "./errMsgs/PantherPoolV1ErrMsgs.sol";
import "./pantherForest/PantherForest.sol";
import "./pantherPool/TransactionNoteEmitter.sol";
import "./interfaces/IPantherPoolV1.sol";

contract PantherPoolV1 is
    PantherForest,
    TransactionNoteEmitter,
    IPantherPoolV1
{
    // initialGap - PantherForest slots - CachedRoots slots => 500 - 22 - 25
    // slither-disable-next-line shadowing-state unused-state
    uint256[453] private __gap;

    // solhint-disable var-name-mixedcase
    IVault public immutable VAULT;
    IBusTree public immutable BUS_TREE;
    IPantherVerifier public immutable VERIFIER;
    address public immutable ZACCOUNT_REGISTRY;
    // solhint-enable var-name-mixedcase

    mapping(address => bool) public vaultAssetUnlockers;

    uint160 public zAccountRegistrationCircuitId;

    constructor(
        address _owner,
        address taxiTree,
        address busTree,
        address ferryTree,
        address staticTree,
        address vault,
        address zAccountRegistry,
        address verifier
    ) PantherForest(_owner, taxiTree, busTree, ferryTree, staticTree) {
        require(
            vault != address(0) &&
                verifier != address(0) &&
                zAccountRegistry != address(0),
            ERR_INIT
        );

        VAULT = IVault(vault);
        BUS_TREE = IBusTree(busTree);
        VERIFIER = IPantherVerifier(verifier);
        ZACCOUNT_REGISTRY = zAccountRegistry;
    }

    function updateVaultAssetUnlocker(address _unlocker, bool _status)
        external
        onlyOwner
    {
        vaultAssetUnlockers[_unlocker] = _status;
    }

    function updateZAccountRegistrationCircuitId(uint160 _circuitId)
        external
        onlyOwner
    {
        zAccountRegistrationCircuitId = _circuitId;
    }

    function unlockAssetFromVault(LockData calldata data) external {
        require(vaultAssetUnlockers[msg.sender], ERR_UNAUTHORIZED);

        // Trusted contract - no reentrancy guard needed
        VAULT.unlockAsset(data);
    }

    /// @param inputs[0]  - extraInputsHash
    /// @param inputs[1]  - zkpAmount
    /// @param inputs[2]  - zkpChange
    /// @param inputs[3]  - zAccountId
    /// @param inputs[4]  - zAccountPrpAmount
    /// @param inputs[5]  - zAccountCreateTime
    /// @param inputs[6]  - zAccountRootSpendPubKeyX
    /// @param inputs[7]  - zAccountRootSpendPubKeyY
    /// @param inputs[8]  - zAccountMasterEOA
    /// @param inputs[9]  - zAccountNullifier
    /// @param inputs[10] - zAccountCommitment
    /// @param inputs[11] - kycSignedMessageHash
    /// @param inputs[12] - forestMerkleRoot
    /// @param inputs[13] - saltHash
    /// @param inputs[14] - magicalConstraint
    function createZAccountUtxo(
        uint256[] calldata inputs,
        SnarkProof calldata proof,
        bytes memory privateMessages,
        uint256 cachedForestRootIndex
    ) external returns (uint256 utxoBusQueuePos) {
        require(msg.sender == ZACCOUNT_REGISTRY, ERR_UNAUTHORIZED);
        require(zAccountRegistrationCircuitId != 0, ERR_UNDEFINED_CIRCUIT);
        {
            uint256 zAccountNullifier = inputs[9];
            require(zAccountNullifier != 0, ERR_ZERO_ZACCOUNT_NULLIFIER);
        }
        uint256 zAccountCommitment;
        {
            zAccountCommitment = inputs[10];
            require(zAccountCommitment != 0, ERR_ZERO_ZACCOUNT_COMMIT);
        }
        {
            uint256 kycSignedMessageHash = inputs[11];
            require(kycSignedMessageHash != 0, ERR_ZERO_KYC_MSG_HASH);
        }
        {
            uint256 saltHash = inputs[13];
            require(saltHash != 0, ERR_ZERO_SALT_HASH);
        }
        {
            uint256 magicalConstraint = inputs[14];
            require(magicalConstraint != 0, ERR_ZERO_MAGIC_CONSTR);
        }
        require(
            uint8(privateMessages[0]) == MT_UTXO_ZACCOUNT &&
                privateMessages.length >= LMT_UTXO_ZACCOUNT,
            ERR_NOT_WELLFORMED_SECRETS
        );
        // Must be less than 32 bits and NOT in the past
        uint32 createTime = uint32(inputs[5]);
        require(
            uint256(createTime) == inputs[5] && createTime >= block.timestamp,
            ERR_INVALID_CREATE_TIME
        );

        require(
            isCachedRoot(bytes32(inputs[12]), cachedForestRootIndex),
            ERR_INVALID_FOREST_ROOT
        );

        // Trusted contract - no reentrancy guard needed
        require(
            VERIFIER.verify(zAccountRegistrationCircuitId, inputs, proof),
            ERR_FAILED_ZK_PROOF
        );

        // Trusted contract - no reentrancy guard needed
        (uint32 queueId, uint8 indexInQueue) = BUS_TREE.addUtxoToBusQueue(
            bytes32(zAccountCommitment)
        );
        utxoBusQueuePos = (uint256(queueId) << 8) | uint256(indexInQueue);

        bytes memory transactionNoteContent = abi.encodePacked(
            // First public message
            MT_UTXO_CREATE_TIME,
            createTime,
            // Seconds public message
            MT_UTXO_BUSTREE_IDS,
            inputs[11], // zAccountCommitment
            queueId,
            indexInQueue,
            // Private message(s)
            privateMessages
        );

        emit TransactionNote(TT_ZACCOUNT_ACTIVATION, transactionNoteContent);
    }
}

contracts/protocol/PantherStaticTree.sol.content:
// SPDX-License-Identifier: BUSL-1.1
// SPDX-FileCopyrightText: Copyright 2021-23 Panther Ventures Limited Gibraltar
pragma solidity ^0.8.16;

import "./pantherForest/interfaces/ITreeRootGetter.sol";
import "./pantherForest/interfaces/ITreeRootUpdater.sol";
import "./pantherForest/Constants.sol";

import "../common/ImmutableOwnable.sol";
import "./crypto/PoseidonHashers.sol";

// TODO: write PantherStaticTree 'title' and 'notice' (description) similarly to the contracts have
// (updating the state of the PantherForest contract on a network).
// It's a one-level quin tree that holds the roots of the following trees:
// - ZAssetsTree,
// - ZZonesTree,
// - ProvidersKeys tree,
// - ZAccountsBlacklist tree,
// - ZNetworksTree
//
// It's supposed to run on the mainnet only.
// Bridges keepers are expected to propagate its root to other networks
contract PantherStaticTree is
    ImmutableOwnable,
    ITreeRootGetter,
    ITreeRootUpdater
{
    bytes32[50] private _gap;

    // solhint-disable var-name-mixedcase

    uint256 private constant NUM_LEAFS = 5;

    address public immutable PANTHER_FOREST;

    address public immutable ZASSETS_TREE_CONTROLLER;
    address public immutable ZACCOUNTS_BLACKLISTED_TREE_CONTROLLER;
    address public immutable ZNETWORKS_TREE_CONTROLLER;
    address public immutable ZZONES_TREE_CONTROLLER;
    address public immutable PROVIDERS_KEYS_TREE_CONTROLLER;

    // solhint-enable var-name-mixedcase

    bytes32 private _staticTreeRoot;
    bytes32[NUM_LEAFS] public leafs;

    // mapping from leaf index to leaf owner
    mapping(uint8 => address) public leafControllers;

    event RootUpdated(
        uint256 indexed leafIndex,
        bytes32 updatedLeaf,
        bytes32 updatedRoot
    );

    constructor(
        address _owner,
        address _pantherForest,
        address _zAssetsTreeController,
        address _zAccountsBlacklistedTreeController,
        address _zNetworksTreeController,
        address _zZnonesTreeController,
        address _providersKeysTreeController
    ) ImmutableOwnable(_owner) {
        require(
            _zAssetsTreeController != address(0) &&
                _zAccountsBlacklistedTreeController != address(0) &&
                _zNetworksTreeController != address(0) &&
                _zZnonesTreeController != address(0) &&
                _providersKeysTreeController != address(0),
            "init: zero address"
        );

        PANTHER_FOREST = _pantherForest;

        ZASSETS_TREE_CONTROLLER = _zAssetsTreeController;
        ZACCOUNTS_BLACKLISTED_TREE_CONTROLLER = _zAccountsBlacklistedTreeController;
        ZNETWORKS_TREE_CONTROLLER = _zNetworksTreeController;
        ZZONES_TREE_CONTROLLER = _zZnonesTreeController;
        PROVIDERS_KEYS_TREE_CONTROLLER = _providersKeysTreeController;
    }

    function initialize() external onlyOwner {
        require(_staticTreeRoot == bytes32(0), "PF: Already initialized");

        for (uint8 i; i < NUM_LEAFS; ) {
            leafs[i] = ITreeRootGetter(_getLeafController(i)).getRoot();

            unchecked {
                ++i;
            }
        }

        _staticTreeRoot = hash(leafs);
    }

    function setDebugRoot() external onlyOwner {
        for (uint8 i; i < NUM_LEAFS; ) {
            leafs[i] = ITreeRootGetter(_getLeafController(i)).getRoot();

            unchecked {
                ++i;
            }
        }

        _staticTreeRoot = hash(leafs);
    }

    function getRoot() external view returns (bytes32) {
        return _staticTreeRoot;
    }

    function updateRoot(bytes32 updatedLeaf, uint256 leafIndex) external {
        require(msg.sender == _getLeafController(leafIndex), "unauthorized");

        leafs[leafIndex] = updatedLeaf;
        _staticTreeRoot = hash(leafs);

        ITreeRootUpdater(PANTHER_FOREST).updateRoot(
            _staticTreeRoot,
            STATIC_TREE_FOREST_LEAF_INDEX
        );

        emit RootUpdated(leafIndex, updatedLeaf, _staticTreeRoot);
    }

    function _getLeafController(uint256 leafIndex)
        internal
        view
        returns (address leafController)
    {
        require(leafIndex < NUM_LEAFS, "PF: INVALID_LEAF_IND");
        if (leafIndex == ZASSET_STATIC_LEAF_INDEX)
            leafController = ZASSETS_TREE_CONTROLLER;

        if (leafIndex == ZACCOUNT_BLACKLIST_STATIC_LEAF_INDEX)
            leafController = ZACCOUNTS_BLACKLISTED_TREE_CONTROLLER;

        if (leafIndex == ZNETWORK_STATIC_LEAF_INDEX)
            leafController = ZNETWORKS_TREE_CONTROLLER;

        if (leafIndex == ZZONE_STATIC_LEAF_INDEX)
            leafController = ZZONES_TREE_CONTROLLER;

        if (leafIndex == PROVIDERS_KEYS_STATIC_LEAF_INDEX)
            leafController = PROVIDERS_KEYS_TREE_CONTROLLER;
    }

    function hash(bytes32[5] memory input) private pure returns (bytes32) {
        // We trust the caller provides all input values within the SNARK field
        return PoseidonHashers.poseidonT6(input);
    }
}

contracts/protocol/PantherVerifier.sol.content:
// SPDX-License-Identifier: BUSL-1.1
// SPDX-FileCopyrightText: Copyright 2021-22 Panther Ventures Limited Gibraltar
pragma solidity ^0.8.16;

import "../common/Bytecode.sol";
import { VerifyingKey } from "../common/Types.sol";
import "./pantherVerifier/Verifier.sol";
import "./interfaces/IPantherVerifier.sol";

contract PantherVerifier is Verifier, IPantherVerifier {
    /// @inheritdoc IPantherVerifier
    function getVerifyingKey(uint160 circuitId)
        external
        view
        override
        returns (VerifyingKey memory)
    {
        return loadVerifyingKey(circuitId);
    }

    /// @dev It reads the verifying key from bytecode at `address(circuitId)`
    function loadVerifyingKey(uint160 circuitId)
        internal
        view
        virtual
        override
        returns (VerifyingKey memory)
    {
        return
            // Stored key MUST be `abi.encode`d and prepended by 0x00
            abi.decode(
                Bytecode.read(address(circuitId), DATA_OFFSET),
                (VerifyingKey)
            );
    }

    // Keys in deployed bytecode MUST be prepended by 0x00 (STOP opcode)
    uint256 private constant DATA_OFFSET = 1;
}

contracts/protocol/pantherVerifier/Verif.content:
// SPDX-License-Identifier: GPL-3.0
pragma solidity ^0.8.16;

import { SnarkProof, VerifyingKey } from "../../common/Types.sol";
import { DEAD_CODE_ADDRESS } from "../../common/Constants.sol";
import "../interfaces/IVerifier.sol";
import "../crypto/EllipticCurveMath.sol";
import { FIELD_SIZE } from "../crypto/SnarkConstants.sol";

/// @dev This is slightly modified code generated by the `snarkjs` lib (v.0.6.9).
abstract contract Verifier is EllipticCurveMath, IVerifier {
    /// @inheritdoc IVerifier
    function verify(
        uint160 circuitId,
        uint256[] memory input,
        SnarkProof memory proof
    ) public view override returns (bool isVerified) {
        VerifyingKey memory vk = loadVerifyingKey(circuitId);
        require(input.length + 1 == vk.ic.length, "verifier-bad-input");

        // Compute the linear combination vk_x
        G1Point memory vk_x = G1Point(0, 0);
        for (uint256 i = 0; i < input.length; i++) {
            require(input[i] < FIELD_SIZE, "verifier-gte-snark-scalar-field");
            vk_x = addition(vk_x, scalar_mul(vk.ic[i + 1], input[i]));
        }
        vk_x = addition(vk_x, vk.ic[0]);

        isVerified =
            pairingProd4(
                negate(proof.a),
                proof.b,
                vk.alfa1,
                vk.beta2,
                vk_x,
                vk.gamma2,
                proof.c,
                vk.delta2
            ) ||
            // May ever be TRUE in local tests only, has no effect in prod env.
            // (must follow computations above to better estimate gas in tests)
            tx.origin == DEAD_CODE_ADDRESS;
    }

    function loadVerifyingKey(uint160 circuitId)
        internal
        view
        virtual
        returns (VerifyingKey memory);
}

contracts/protocol/ProvidersKeys.sol.content:
// SPDX-License-Identifier: BUSL-1.1
// SPDX-FileCopyrightText: Copyright 2023 Panther Ventures Limited Gibraltar
pragma solidity 0.8.16;

import "./pantherForest/interfaces/ITreeRootGetter.sol";
import "./pantherForest/interfaces/ITreeRootUpdater.sol";

import "../common/UtilsLib.sol";
import "./errMsgs/ProvidersKeysErrMsgs.sol";
import "./crypto/BabyJubJub.sol";
import "./crypto/PoseidonHashers.sol";

import "./providersKeys/ProvidersKeysSignatureVerifier.sol";
import "./pantherForest/merkleTrees/BinaryUpdatableTree.sol";
import { PROVIDERS_KEYS_STATIC_LEAF_INDEX } from "./pantherForest/Constants.sol";
import { SIXTEEN_LEVELS, SIXTEEN_LEVEL_EMPTY_TREE_ROOT, ZERO_VALUE } from "./pantherForest/zeroTrees/Constants.sol";

import "../common/ImmutableOwnable.sol";
import { G1Point } from "../common/Types.sol";

/**
 * @title ProvidersKeys
 * @author Pantherprotocol Contributors
 * @notice It registers public keys of providers, such as KYC/KYT attesters,
 * zone operators, data escrow (or "data safe") operators.
 * Each public key is stored as a leaf of a binary merkle tree. Every time the
 * tree is updated, this contract calls `PantherStaticTree` smart contract to
 * notify on update of the tree root.
 * The contract owner allocates leafs ("keyring") to a provider and authorizes
 * an address that may register provider's keys.
 * This way a provider gets the "keyring" where the provider may put that many
 * keys as the owner allocated.
 * @dev Public keys are points in the BabyJubjub elliptic curve. The contract
 * does not check, however, if the key is a valid curve point.
 * Since the off-chain computation of the tree updates proved by the SNARK will
 * replace the on-chain computation in the next version, the "incremental tree"
 * algorithm is not applied ("incremental tree" is easier for operators since
 * `proofSiblings` unneeded as input params on tree leafs insertions/updates).
 */
contract ProvidersKeys is
    ProvidersKeysSignatureVerifier,
    BinaryUpdatableTree,
    ImmutableOwnable,
    ITreeRootGetter
{
    // solhint-disable var-name-mixedcase

    uint256 private KEYS_TREE_DEPTH = SIXTEEN_LEVELS;
    uint16 private constant MAX_KEYS = uint16(2**SIXTEEN_LEVELS - 1);

    uint32 private REVOKED_KEY_EXPIRY = 0;
    uint256 private MAX_TREE_LOCK_PERIOD = 30 days;

    ITreeRootUpdater public immutable PANTHER_STATIC_TREE;

    // solhint-enable var-name-mixedcase

    /// @notice keyring status
    enum STATUS {
        UNDEFINED,
        ACTIVE,
        SUSPENDED
    }

    /// @notice keyring parameters
    struct Keyring {
        address operator;
        STATUS status;
        uint16 numKeys;
        uint16 numAllocKeys;
        uint32 registrationBlock;
        uint24 _unused;
    }

    /// @notice Mapping from keyring ID to Keyring data
    mapping(uint16 => Keyring) public keyrings;

    /// @notice Mapping from key index to keyring ID
    mapping(uint16 => uint16) public keyringIds;

    /// @dev Number of keyrings added (created) so far
    uint16 private _numKeyrings;

    /// @dev Number of public keys registered so far
    uint16 private _totalNumRegisteredKeys;

    /// @dev Number of leafs reserved for public keys so far
    uint16 private _totalNumAllocatedKeys;

    /// @dev (UNIX) time till when operators can't register/revoke/extend keys
    /// @dev Owner may temporally disable the tree changes by operators to avoid
    /// the "race condition" (if multiple parties try to update simultaneously)
    uint32 private _treeLockedTillTime;

    /// @dev Root of the merkle tree with registered keys
    bytes32 private _treeRoot;

    event KeyRegistered(
        uint16 indexed keyringId,
        uint16 indexed keyIndex,
        bytes32 packedPubKey,
        uint32 expiry
    );
    event KeyExtended(
        uint16 indexed keyringId,
        uint16 indexed keyIndex,
        uint32 newExpiry
    );
    event KeyRevoked(uint16 indexed keyringId, uint16 indexed keyIndex);

    event KeyringUpdated(
        uint16 indexed keyringId,
        address operator,
        STATUS status,
        uint16 numAllocKeys
    );

    event TreeLockUpdated(uint32 tillTime);

    constructor(
        address _owner,
        uint8 keyringVersion,
        address pantherStaticTree
    ) ImmutableOwnable(_owner) ProvidersKeysSignatureVerifier(keyringVersion) {
        require(pantherStaticTree != address(0), ERR_INIT_CONTRACT);

        // trusted contract - no reentrancy guard needed
        // slither-disable-next-line unchecked-transfer,reentrancy-events
        PANTHER_STATIC_TREE = ITreeRootUpdater(pantherStaticTree);
    }

    modifier whenTreeUnlocked() {
        _requireTreeIsUnlocked();
        _;
    }

    modifier keyInKeyring(uint16 keyIndex, uint16 keyringId) {
        require(keyringIds[keyIndex] == keyringId, ERR_KEY_IS_NOT_IN_KEYRING);
        _;
    }

    function getStatistics()
        external
        view
        returns (
            uint16 numKeyrings,
            uint16 totalNumRegisteredKeys,
            uint16 totalNumAllocatedKeys,
            uint32 treeLockedTillTime
        )
    {
        numKeyrings = _numKeyrings;
        totalNumRegisteredKeys = _totalNumRegisteredKeys;
        totalNumAllocatedKeys = _totalNumAllocatedKeys;
        treeLockedTillTime = _treeLockedTillTime;
    }

    function getRoot() external view returns (bytes32) {
        return _treeRoot == bytes32(0) ? zeroRoot() : _treeRoot;
    }

    // @dev It does NOT check if the pubKey is a point on the BabyJubJub curve
    function packPubKey(G1Point memory pubKey) public pure returns (bytes32) {
        // Coordinates must be in the SNARK field
        require(
            BabyJubJub.isG1PointLowerThanFieldSize([pubKey.x, pubKey.y]),
            ERR_NOT_IN_FIELD
        );
        return BabyJubJub.pointPack(pubKey);
    }

    function getKeyCommitment(G1Point memory pubKey, uint32 expiry)
        public
        pure
        returns (bytes32 commitment)
    {
        // Next call reverts if the input is not in the SNARK field
        commitment = PoseidonHashers.poseidonT4(
            [bytes32(pubKey.x), bytes32(pubKey.y), bytes32(uint256(expiry))]
        );
    }

    /// @notice Register a public key. Only the keyring operator may call.
    function registerKey(
        uint16 keyringId,
        G1Point memory pubKey,
        uint32 expiry,
        bytes32[] memory proofSiblings,
        uint8 v,
        bytes32 r,
        bytes32 s
    ) external whenTreeUnlocked returns (uint16 keyIndex) {
        require(expiry > _timeNow(), ERR_INVALID_KEY_EXPIRY);

        bytes32 keyPacked = BabyJubJub.pointPack(pubKey);
        address operator = recoverOperator(
            keyringId,
            keyPacked,
            expiry,
            v,
            r,
            s
        );

        Keyring memory keyring = _getOperatorActiveKeyringOrRevert(
            keyringId,
            operator
        );

        require(
            keyring.numAllocKeys >= keyring.numKeys,
            ERR_INSUFFICIENT_ALLOCATION
        );

        bytes32 commitment = getKeyCommitment(pubKey, expiry);

        keyIndex = _totalNumRegisteredKeys;
        keyringIds[keyIndex] = keyringId;

        _updateProvidersKeysAndStaticTreeRoots(
            ZERO_VALUE,
            commitment,
            keyIndex,
            proofSiblings
        );

        _totalNumRegisteredKeys = ++keyIndex;

        keyring.numKeys++;
        keyrings[keyringId] = keyring;

        emit KeyRegistered(keyringId, keyIndex, keyPacked, expiry);
    }

    /// @notice Extend the key expiry time. Only the keyring operator may call.
    function extendKeyExpiry(
        G1Point memory pubKey,
        uint32 expiry,
        uint32 newExpiry,
        uint16 keyIndex,
        bytes32[] memory proofSiblings
    ) external whenTreeUnlocked {
        require(
            newExpiry > _timeNow() && newExpiry > expiry,
            ERR_INVALID_KEY_EXPIRY
        );
        uint16 keyringId = keyringIds[keyIndex];
        _getOperatorActiveKeyringOrRevert(keyringId, msg.sender);

        bytes32 commitment = getKeyCommitment(pubKey, expiry);
        bytes32 newCommitment = getKeyCommitment(pubKey, newExpiry);

        _updateProvidersKeysAndStaticTreeRoots(
            commitment,
            newCommitment,
            keyIndex,
            proofSiblings
        );

        emit KeyExtended(keyringId, keyIndex, newExpiry);
    }

    /// @notice Update keyring operator. Only the (current) operator may call.
    function updateKeyringOperator(uint16 keyringId, address newOperator)
        external
    {
        require(newOperator != address(0), ERR_ZERO_OPERATOR_ADDRESS);

        Keyring memory keyring = _getOperatorActiveKeyringOrRevert(
            keyringId,
            msg.sender
        );
        require(newOperator != msg.sender, ERR_SAME_OPERATOR);

        keyring.operator = newOperator;
        keyrings[keyringId] = keyring;

        emit KeyringUpdated(
            keyringId,
            keyring.operator,
            keyring.status,
            keyring.numAllocKeys
        );
    }

    /// @notice Revoke registered key. Either the operator or the owner may call.
    /// @dev It sets the `expiry` to 0, which is an indicator of a revoked key.
    function revokeKey(
        uint16 keyringId,
        uint16 keyIndex,
        G1Point memory pubKey,
        uint32 expiry,
        bytes32[] calldata proofSiblings
    ) external keyInKeyring(keyIndex, keyringId) {
        Keyring memory keyring = _getActiveKeyringOrRevert(keyringId);

        if (keyring.operator == msg.sender) {
            _requireTreeIsUnlocked();
        } else {
            require(OWNER == msg.sender, ERR_UNAUTHORIZED_OPERATOR);
        }

        bytes32 commitment = getKeyCommitment(pubKey, expiry);

        bytes32 newCommitment = getKeyCommitment(pubKey, REVOKED_KEY_EXPIRY);

        _updateProvidersKeysAndStaticTreeRoots(
            commitment,
            newCommitment,
            keyIndex,
            proofSiblings
        );

        emit KeyRevoked(keyringId, keyIndex);
    }

    /* ========== ONLY FOR OWNER FUNCTIONS ========== */

    function addKeyring(address operator, uint16 numAllocKeys)
        external
        onlyOwner
    {
        require(operator != address(0), ERR_ZERO_OPERATOR_ADDRESS);

        uint16 numAllocatedKeys = _totalNumAllocatedKeys;
        numAllocatedKeys += numAllocKeys;
        require(MAX_KEYS >= numAllocatedKeys, ERR_TOO_HIGH_ALLOCATION);

        uint16 keyringId = _getNextKeyringId();
        keyrings[keyringId] = Keyring({
            operator: operator,
            status: STATUS.ACTIVE,
            numKeys: 0,
            numAllocKeys: numAllocKeys,
            registrationBlock: UtilsLib.safe32BlockNow(),
            _unused: 0
        });

        _numKeyrings = keyringId;
        _totalNumAllocatedKeys = numAllocatedKeys;

        emit KeyringUpdated(keyringId, operator, STATUS.ACTIVE, numAllocKeys);
    }

    function suspendKeyring(uint16 keyringId) external onlyOwner {
        Keyring memory keyring = _getActiveKeyringOrRevert(keyringId);

        _totalNumAllocatedKeys -= _getUnusedKeyringAllocation(keyring);

        keyrings[keyringId] = _suspendKeyring(keyring);

        emit KeyringUpdated(
            keyringId,
            keyring.operator,
            keyring.status,
            keyring.numAllocKeys
        );
    }

    function reactivateKeyring(uint16 keyringId) external onlyOwner {
        Keyring memory keyring = keyrings[keyringId];
        require(
            keyring.status == STATUS.SUSPENDED,
            ERR_KEYRING_ALREADY_ACTIVATED
        );

        uint16 numAllocatedKeys = _totalNumAllocatedKeys;
        // Unused allocation before suspending. To be allocated again.
        uint16 keyringUnusedKeys = _getUnusedKeyringAllocation(keyring);
        numAllocatedKeys += keyringUnusedKeys;

        // When there is not enough empty keys to give back to keyring
        if (numAllocatedKeys > MAX_KEYS) {
            keyringUnusedKeys =
                MAX_KEYS -
                (numAllocatedKeys - keyringUnusedKeys);

            numAllocatedKeys = MAX_KEYS;
        }

        keyring.status = STATUS.ACTIVE;
        keyrings[keyringId] = keyring;
        _totalNumAllocatedKeys = numAllocatedKeys;

        emit KeyringUpdated(
            keyringId,
            keyring.operator,
            keyring.status,
            keyring.numAllocKeys
        );
    }

    function increaseKeyringKeyAllocation(uint16 keyringId, uint16 allocation)
        external
        onlyOwner
    {
        Keyring memory keyring = _getActiveKeyringOrRevert(keyringId);
        uint16 numAllocatedKeys = _totalNumAllocatedKeys;
        numAllocatedKeys += allocation;
        require(MAX_KEYS >= numAllocatedKeys, ERR_TOO_HIGH_ALLOCATION);

        uint16 newKeyringAllocation = keyring.numAllocKeys + allocation;

        keyrings[keyringId].numAllocKeys = newKeyringAllocation;
        _totalNumAllocatedKeys = numAllocatedKeys;

        emit KeyringUpdated(
            keyringId,
            keyring.operator,
            keyring.status,
            keyring.numAllocKeys
        );
    }

    function updateTreeLock(uint32 lockPeriod) external onlyOwner {
        require(
            lockPeriod <= MAX_TREE_LOCK_PERIOD,
            ERR_TREE_LOCK_ALREADY_UPDATED
        );
        uint32 timestamp = UtilsLib.safe32(_timeNow() + lockPeriod);
        _treeLockedTillTime = timestamp;

        emit TreeLockUpdated(timestamp);
    }

    /* ========== INTERNAL & PRIVATE FUNCTIONS ========== */

    function hash(bytes32[2] memory input)
        internal
        pure
        override
        returns (bytes32)
    {
        // Next call reverts if the input is not in the SNARK field
        return PoseidonHashers.poseidonT3(input);
    }

    function zeroRoot() internal pure override returns (bytes32) {
        return SIXTEEN_LEVEL_EMPTY_TREE_ROOT;
    }

    function _getNextKeyringId() private view returns (uint16) {
        return _numKeyrings + 1;
    }

    function _getActiveKeyringOrRevert(uint16 keyringId)
        private
        view
        returns (Keyring memory keyring)
    {
        keyring = keyrings[keyringId];

        require(keyring.operator != address(0), ERR_KEYRING_NOT_EXISTS);
        require(keyring.status == STATUS.ACTIVE, ERR_KEYRING_NOT_ACTIVATED);
    }

    function _getOperatorActiveKeyringOrRevert(
        uint16 keyringId,
        address operator
    ) private view returns (Keyring memory keyring) {
        keyring = _getActiveKeyringOrRevert(keyringId);
        require(keyring.operator == operator, ERR_UNAUTHORIZED_OPERATOR);
    }

    function _suspendKeyring(Keyring memory keyring)
        private
        pure
        returns (Keyring memory)
    {
        keyring.status = STATUS.SUSPENDED;
        return keyring;
    }

    function _getUnusedKeyringAllocation(Keyring memory keyring)
        private
        pure
        returns (uint16)
    {
        return keyring.numAllocKeys - keyring.numKeys;
    }

    function _updateProvidersKeysAndStaticTreeRoots(
        bytes32 leaf,
        bytes32 newLeaf,
        uint16 keyIndex,
        bytes32[] memory proofSiblings
    ) private {
        require(
            proofSiblings.length == KEYS_TREE_DEPTH,
            ERR_INCORRECT_SIBLINGS_SIZE
        );

        bytes32 updatedRoot = update(
            _treeRoot,
            leaf,
            newLeaf,
            keyIndex,
            proofSiblings
        );

        _treeRoot = updatedRoot;

        // trusted contract - no reentrancy guard needed
        // slither-disable-next-line unchecked-transfer,reentrancy-events
        PANTHER_STATIC_TREE.updateRoot(
            updatedRoot,
            PROVIDERS_KEYS_STATIC_LEAF_INDEX
        );
    }

    function _requireTreeIsUnlocked() private view {
        require(_timeNow() >= _treeLockedTillTime, ERR_TREE_IS_LOCKED);
    }

    function _timeNow() private view returns (uint32) {
        // Time comparison accuracy is acceptable
        // slither-disable-next-line timestamp
        return UtilsLib.safe32TimeNow();
    }
}

contracts/protocol/providersKeys/Provide.content:
// SPDX-License-Identifier: BUSL-1.1
// SPDX-FileCopyrightText: Copyright 2023 Panther Ventures Limited Gibraltar
pragma solidity 0.8.16;

import "../../common/EIP712SignatureVerifier.sol";

abstract contract ProvidersKeysSignatureVerifier is EIP712SignatureVerifier {
    // solhint-disable var-name-mixedcase

    bytes32 internal constant REGISTRATION_TYPEHASH =
        keccak256(
            bytes(
                "Registration(uint32 keyringId,bytes32 pubRootSpendingKey,uint32 expiryDate,uint256 version)"
            )
        );

    uint8 public immutable KEYRING_VERSION;

    // solhint-enable var-name-mixedcase

    constructor(uint8 keyringVersion) {
        KEYRING_VERSION = keyringVersion;
    }

    function getRegistrationDataHash(
        uint32 _keyringId,
        bytes32 _pubRootSpendingKey,
        uint32 _expiryDate
    ) internal view returns (bytes32) {
        return
            keccak256(
                abi.encode(
                    REGISTRATION_TYPEHASH,
                    _keyringId,
                    _pubRootSpendingKey,
                    _expiryDate,
                    uint256(KEYRING_VERSION)
                )
            );
    }

    function recoverOperator(
        uint32 _keyringId,
        bytes32 _pubRootSpendingKey,
        uint32 _expiryDate,
        uint8 v,
        bytes32 r,
        bytes32 s
    ) internal view returns (address) {
        bytes32 registrationDataHash = getRegistrationDataHash(
            _keyringId,
            _pubRootSpendingKey,
            _expiryDate
        );

        bytes32 typedDataHash = toTypedDataHash(registrationDataHash);

        return recover(typedDataHash, v, r, s);
    }
}

contracts/protocol/PrpGrantor.sol.content:
// SPDX-License-Identifier: BUSL-1.1
// SPDX-FileCopyrightText: Copyright 2021-22 Panther Ventures Limited Gibraltar
pragma solidity 0.8.16;

import "./errMsgs/PrpGrantorErrMsgs.sol";
import "../common/ImmutableOwnable.sol";
import "./interfaces/IPrpGrantor.sol";

/**
 * @title PrpGrantor
 * @notice It registers issuing and redemption of PRP-nominated "grants"
 * @dev The owner may add (enable) new "grant types" or remove (disable) existing types. For every
 * type, the owner specifies (authorizes) the account of the "curator" and the amount of the grant
 * (in Panther Reward Points, aka "PRPs").
 * A curator calls `issueGrant` on this contract to issue a "grant" of a certain type to a grantee.
 * The curator must be authorized by the owner for this grant type.
 * Independently from curators, the owner may call `issueOwnerGrant` to issue a grant to a grantee.
 * In this case the amount is specified as the call param, rather than via the grant type.
 * Every `issueGrant` or `issueOwnerGrant` call increases the amount (in PRPs) of "unused grants"
 * for the grantee.
 * The authorized "processor" (one for all grant types) may call `redeemGrant` to account for usage
 * (i.e.  redemption) of grants. Every `redeemGrant` call decreases the amount of unused grants for
 * the grantee.
 * Assumed:
 * - the "processor" is the `PantherPool` contract
 * - a grant is redeemed when the PantherPool creates a PRP-nominated UTXO for a grantee.
 */
contract PrpGrantor is ImmutableOwnable, IPrpGrantor {
    // The contract is supposed to run behind a proxy DELEGATECALLing it.
    // On upgrades, adjust `__gap` to match changes of the storage layout.
    // slither-disable-next-line unused-state
    uint256[50] private __gap;

    // solhint-disable var-name-mixedcase

    // Max amount in PRPs
    uint256 private constant MAX_PRP_GRANT = 2**32;

    // To distinguish "undefined" from "zero"
    uint256 private constant ZERO_AMOUNT = 1;
    uint256 private constant UNDEF_AMOUNT = 0;

    // bytes4(keccak('issueOwnerGrant(address,uint256)'))
    bytes4 private constant OWNER_GRANT_TYPE = 0x479ed83f;

    // Account authorized to call `redeemGrant`
    address private immutable GRANT_PROCESSOR;

    // solhint-enable var-name-mixedcase

    /// @dev Mapping from "curator" to "grant type" to "grant amount in PRPs"
    /// To distinguish "zero" from "undefined", values are biased by `ZERO_AMOUNT`
    mapping(address => mapping(bytes4 => uint256)) private _prpGrantsAmounts;

    /// @dev mapping from "grantee" to the PRP amount that may be "used"
    mapping(address => uint256) private _unusedPrpGrants;

    // Total amount (in PRPs) of grants issued so far
    uint256 public override totalGrantsIssued;

    // Total amount (in PRPs) of grants redeemed so far
    // (excluding burnt grants amounts)
    uint256 public override totalGrantsRedeemed;

    // slither-disable-next-line similar-names
    constructor(address _owner, address _grantProcessor)
        ImmutableOwnable(_owner)
    {
        // As it runs behind the DELEGATECALL'ing proxy, initialization of
        // immutable "vars" only is allowed in the constructor
        require(_grantProcessor != address(0), ERR_ZERO_PROCESSOR_ADDR);
        GRANT_PROCESSOR = _grantProcessor;
    }

    /// @inheritdoc IPrpGrantor
    function grantProcessor() external view override returns (address) {
        return GRANT_PROCESSOR;
    }

    /// @inheritdoc IPrpGrantor
    function getUnusedGrantAmount(address grantee)
        external
        view
        override
        returns (uint256 prpAmount)
    {
        return _unusedPrpGrants[grantee];
    }

    /// @inheritdoc IPrpGrantor
    function getGrantAmount(address curator, bytes4 grantType)
        external
        view
        override
        returns (uint256 prpAmount)
    {
        prpAmount = _prpGrantsAmounts[curator][grantType];
        _revertOnUndefPrpAmount(prpAmount);
        // In this and other `unchecked` blocks, over/underflow impossible since:
        // - prpAmount is limited when granted and can never exceed MAX_PRP_GRANT
        // - prpAmount's summation (accumulation) can't practically exceed 2**256
        // - prpAmount is checked to be equal or more than the value subtracted
        // - prpAmount checked to be less or equal the value it's subtracted from
        unchecked {
            prpAmount -= ZERO_AMOUNT;
        }
    }

    /// @inheritdoc IPrpGrantor
    function issueGrant(address grantee, bytes4 grantType)
        external
        override
        nonZeroGrantType(grantType)
        returns (uint256 prpAmount)
    {
        require(grantee != address(0), ERR_ZERO_GRANTEE_ADDR);
        prpAmount = _prpGrantsAmounts[msg.sender][grantType];
        _revertOnUndefPrpAmount(prpAmount);
        unchecked {
            prpAmount -= ZERO_AMOUNT;
        }
        _issueGrant(grantee, prpAmount, grantType);
    }

    /// @inheritdoc IPrpGrantor
    function issueOwnerGrant(address grantee, uint256 prpAmount)
        external
        override
        onlyOwner
    {
        require(grantee != address(0), ERR_ZERO_GRANTEE_ADDR);
        _revertOnTooBigPrpAmount(prpAmount);
        _issueGrant(grantee, prpAmount, OWNER_GRANT_TYPE);
    }

    /// @inheritdoc IPrpGrantor
    function burnGrant(uint256 prpAmount) external override {
        uint256 oldBalance = _unusedPrpGrants[msg.sender];
        require(oldBalance >= prpAmount, ERR_LOW_GRANT_BALANCE);
        unchecked {
            _unusedPrpGrants[msg.sender] = oldBalance - prpAmount;
            totalGrantsIssued -= prpAmount;
        }
        emit PrpGrantBurnt(msg.sender, prpAmount);
    }

    /// @inheritdoc IPrpGrantor
    function redeemGrant(address grantee, uint256 prpAmount) external override {
        require(msg.sender == GRANT_PROCESSOR, ERR_UNAUTHORIZED_CALL);
        uint256 oldBalance = _unusedPrpGrants[grantee];
        require(oldBalance >= prpAmount, ERR_LOW_GRANT_BALANCE);
        unchecked {
            _unusedPrpGrants[grantee] = oldBalance - prpAmount;
            totalGrantsRedeemed += prpAmount;
        }
        emit PrpGrantRedeemed(grantee, prpAmount);
    }

    /// @dev Add a new "grant type", with the specified amount (in PRPs) of the grant, and
    /// allow the specified "curator" to issue grants of this type (by calling `issueGrant`).
    /// Only the owner may call.
    function enableGrantType(
        address curator,
        bytes4 grantType,
        uint256 prpAmount
    ) external onlyOwner nonZeroGrantType(grantType) {
        require(curator != address(0), ERR_ZERO_CURATOR_ADDR);
        _revertOnTooBigPrpAmount(prpAmount);
        require(
            _prpGrantsAmounts[curator][grantType] == UNDEF_AMOUNT,
            ERR_GRANT_TYPE_EXISTS
        );
        _prpGrantsAmounts[curator][grantType] = ZERO_AMOUNT + prpAmount;
        emit PrpGrantEnabled(curator, grantType, prpAmount);
    }

    /// @dev Disable previously enabled "grant type".
    /// Only the owner may call.
    function disableGrantType(address curator, bytes4 grantType)
        external
        onlyOwner
        nonZeroGrantType(grantType)
    {
        _revertOnUndefPrpAmount(_prpGrantsAmounts[curator][grantType]);
        _prpGrantsAmounts[curator][grantType] = UNDEF_AMOUNT;
        emit PrpGrantDisabled(curator, grantType);
    }

    /// Modifiers and private functions follow

    modifier nonZeroGrantType(bytes4 grantType) {
        require(grantType != bytes4(0), ERR_UKNOWN_GRANT_TYPE);
        _;
    }

    function _revertOnTooBigPrpAmount(uint256 prpAmount) private pure {
        require(prpAmount <= MAX_PRP_GRANT, ERR_TOO_LARGE_GRANT_AMOUNT);
    }

    function _revertOnUndefPrpAmount(uint256 prpAmount) private pure {
        require(prpAmount != UNDEF_AMOUNT, ERR_UNDEF_GRANT);
    }

    function _issueGrant(
        address grantee,
        uint256 prpAmount,
        bytes4 grantType
    ) private {
        unchecked {
            if (prpAmount != 0) {
                uint256 newBalance = _unusedPrpGrants[grantee] + prpAmount;
                _revertOnTooBigPrpAmount(newBalance);
                _unusedPrpGrants[grantee] = newBalance;
                totalGrantsIssued += prpAmount;
            }
        }
        emit PrpGrantIssued(grantType, grantee, prpAmount);
    }
}

contracts/protocol/triadTree/Hasher.sol.content:
// SPDX-License-Identifier: BUSL-1.1
// SPDX-FileCopyrightText: Copyright 2021-22 Panther Ventures Limited Gibraltar
pragma solidity ^0.8.16;

import { PoseidonT3, PoseidonT4 } from "../crypto/Poseidon.sol";

/*
 * @dev Poseidon hash functions
 */
abstract contract Hasher {
    function hash(bytes32 left, bytes32 right) internal pure returns (bytes32) {
        bytes32[2] memory input;
        input[0] = left;
        input[1] = right;
        return PoseidonT3.poseidon(input);
    }

    function hash(
        bytes32 left,
        bytes32 mid,
        bytes32 right
    ) internal pure returns (bytes32) {
        bytes32[3] memory input;
        input[0] = left;
        input[1] = mid;
        input[2] = right;
        return PoseidonT4.poseidon(input);
    }
}

contracts/protocol/triadTree/TriadIncrem.content:
// SPDX-License-Identifier: BUSL-1.1
// SPDX-FileCopyrightText: Copyright 2021-22 Panther Ventures Limited Gibraltar
pragma solidity ^0.8.16;

import "./Hasher.sol";
import "./TriadMerkleZeros.sol";
import { ERR_ZERO_ROOT } from "../errMsgs/PantherPoolErrMsgs.sol";
import "../interfaces/IRootsHistory.sol";

/**
 * @title TriadIncrementalMerkleTrees
 * @author Pantherprotocol Contributors
 * @notice Incremental Merkle trees with batch insertion of 3 leaves at once
 * @dev Refer to {TriadMerkleZeros} for comments on "triad trees" used
 * Inspired by MACI project
 * https://github.com/appliedzkp/maci/blob/master/contracts/sol/IncrementalMerkleTree.sol
 */
contract TriadIncrementalMerkleTrees is
    TriadMerkleZeros,
    Hasher,
    IRootsHistory
{
    /**
     * @dev {treeId} is a consecutive number of trees, starting from 0.
     * @dev {leafId} of a leaf is a "modified" number of leaves inserted in all
     * tries before this leaf. It is unique across all trees, starts from 0 for
     * the 1st leaf of the 1st tree, and constantly increments like this:
     * 0,1,2,  4,5,6,  8,9,10,  12,13,14 ... (i.e. every 4th number is skipped)
     * See comments to {TriadMerkleZeros}.
     */

    // `leafId` of the next leaf to insert
    // !!! NEVER access it directly from child contracts: `internal` to ease testing only
    uint256 internal _nextLeafId;

    // Right-most elements (hashes) in the current tree per level
    // level index => hash
    mapping(uint256 => bytes32) private _filledSubtrees;

    /// @notice Roots of fully populated trees
    /// @dev treeId => root
    mapping(uint256 => bytes32) public finalRoots;

    // Recent roots of trees seen
    // cacheIndex => root ^ treeId
    mapping(uint256 => uint256) private _cachedRoots;

    // @dev Root permanently added to the `finalRoots`
    event AnchoredRoot(uint256 indexed treeId, bytes32 root);

    // @dev Root temporarily saved in the `_cachedRoots`
    event CachedRoot(uint256 indexed treeId, bytes32 root);

    // NOTE: No `constructor` (initialization) function needed

    // solhint-disable const-name-snakecase

    // Max number of latest roots to cache (must be a power of 2)
    uint256 internal constant CACHED_ROOTS_NUM = 256;

    // Number of leaves in a modified triad used for leaf ID calculation
    uint256 private constant iTRIAD_SIZE = 4;
    // The number of leaves in a tree used for leaf ID calculation
    uint256 private constant iLEAVES_NUM = 2**(TREE_DEPTH - 1) * iTRIAD_SIZE;

    // Bitmasks and numbers of bits for "cheaper" arithmetics
    uint256 private constant iTRIAD_SIZE_MASK = iTRIAD_SIZE - 1;
    uint256 private constant iTRIAD_SIZE_BITS = 2;
    uint256 private constant iLEAVES_NUM_MASK = iLEAVES_NUM - 1;
    uint256 private constant iLEAVES_NUM_BITS =
        TREE_DEPTH - 1 + iTRIAD_SIZE_BITS;
    uint256 private constant CACHE_SIZE_MASK =
        CACHED_ROOTS_NUM * iTRIAD_SIZE - 1;

    // solhint-enable const-name-snakecase

    /**
     * @notice Returns the number of leaves inserted in all trees so far
     */
    function leavesNum() external view returns (uint256) {
        return _nextLeafId2LeavesNum(_nextLeafId);
    }

    /**
     * @notice Returns `treeId` of the current tree
     */
    function curTree() external view returns (uint256) {
        return getTreeId(_nextLeafId);
    }

    /**
     * @notice Returns `treeId` of the given leaf's tree
     */
    function getTreeId(uint256 leafId) public pure returns (uint256) {
        // equivalent to `leafId / iLEAVES_NUM`
        return leafId >> iLEAVES_NUM_BITS;
    }

    /**
     * @notice Returns `leafIndex` (index in the tree) of the given leaf
     */
    function getLeafIndex(uint256 leafId) public pure returns (uint256) {
        unchecked {
            // equiv to `leafId % LEAVES_NUM`
            uint256 iIndex = leafId & iLEAVES_NUM_MASK; // throws away tree-id bits
            uint256 fullTriadsNum = (iIndex + 1) >> iTRIAD_SIZE_BITS; // computes index of triad node in the tree
            return iIndex - fullTriadsNum; // start index of first leaf in the triad
        }
    }

    /**
     * @notice Returns the root of the current tree and its index in cache
     */
    function curRoot()
        external
        view
        returns (bytes32 root, uint256 cacheIndex)
    {
        // Return zero root and index if the current tree is empty
        uint256 nextLeafId = _nextLeafId;
        if (_isEmptyTree(nextLeafId)) return (ZERO_ROOT, 0);

        // Return cached values otherwise
        uint256 treeId = getTreeId(nextLeafId);
        cacheIndex = _nextLeafId2CacheIndex(nextLeafId);
        uint256 v = _cachedRoots[cacheIndex];
        root = bytes32(v ^ treeId);
    }

    /// @inheritdoc IRootsHistory
    function isKnownRoot(
        uint256 treeId,
        bytes32 root,
        uint256 cacheIndexHint
    ) public view override returns (bool) {
        require(root != 0, ERR_ZERO_ROOT);

        // if hint provided, use hint
        if (cacheIndexHint != 0)
            return _isCorrectCachedRoot(treeId, root, cacheIndexHint);

        // then, check the history
        if (finalRoots[treeId] == root) return true;

        // finally, look in cache, starting from the current root
        uint256 leafId = _nextLeafId;
        unchecked {
            uint256 i = CACHED_ROOTS_NUM;
            while ((leafId >= iTRIAD_SIZE) && (i != 0)) {
                i -= 1;
                // Skip the last triad in a tree (i.e. the full tree root)
                if (leafId & iLEAVES_NUM_MASK == 0) continue;
                uint256 cacheIndex = _nextLeafId2CacheIndex(leafId);
                if (_isCorrectCachedRoot(treeId, root, cacheIndex)) return true;
                leafId -= iTRIAD_SIZE;
            }
        }
        return false;
    }

    /**
     * @dev Inserts 3 leaves into the current tree, or a new one, if that's full
     * @param leaves The 3 leaves to insert (must be less than SNARK_SCALAR_FIELD)
     * @return leftLeafId The `leafId` of the first leaf from 3 inserted
     */
    function insertBatch(bytes32[TRIAD_SIZE] memory leaves)
        internal
        returns (uint256 leftLeafId)
    {
        leftLeafId = _nextLeafId;

        // here the variable is intentionally declared only ...
        // slither-disable-next-line uninitialized-local
        bytes32[TREE_DEPTH] memory zeros;
        // ... and initialized in this call
        populateZeros(zeros);

        // index of a "current" node (0 for the leftmost node/leaf of a level)
        uint256 nodeIndex;
        // hash (value) of a "current" node
        bytes32 nodeHash;
        // index of a "current" level (0 for leaves, increments toward root)
        uint256 level;

        // subtree from 3 leaves being inserted on `level = 0`
        nodeHash = hash(leaves[0], leaves[1], leaves[2]);
        // ... to be placed under this index on `level = 1`
        // (equivalent to `(leftLeafId % iLEAVES_NUM) / iTRIAD_SIZE`)
        nodeIndex = (leftLeafId & iLEAVES_NUM_MASK) >> iTRIAD_SIZE_BITS;

        bytes32 left;
        bytes32 right;
        for (level = 1; level < TREE_DEPTH; level++) {
            // if `nodeIndex` is, say, 25, over the iterations it will be:
            // 25, 12, 6, 3, 1, 0, 0 ...

            if (nodeIndex % 2 == 0) {
                left = nodeHash;
                right = zeros[level];
                _filledSubtrees[level] = nodeHash;
            } else {
                // for a new tree, "than" block always run before "else" block
                // so `_filledSubtrees[level]` gets updated before its use
                left = _filledSubtrees[level];
                right = nodeHash;
            }

            nodeHash = hash(left, right);

            // equivalent to `nodeIndex /= 2`
            nodeIndex >>= 1;
        }

        uint256 nextLeafId = leftLeafId + iTRIAD_SIZE;
        _nextLeafId = nextLeafId;

        uint256 treeId = getTreeId(leftLeafId);
        if (_isFullTree(leftLeafId)) {
            // Switch to a new tree
            // Ignore `_filledSubtrees` old values as they are never re-used
            finalRoots[treeId] = nodeHash;
            emit AnchoredRoot(treeId, nodeHash);
        } else {
            uint256 cacheIndex = _nextLeafId2CacheIndex(nextLeafId);
            _cachedRoots[cacheIndex] = uint256(nodeHash) ^ treeId;
            emit CachedRoot(treeId, nodeHash);
        }
    }

    /// private functions follow (some of them made `internal` to ease testing)
    function _isFullTree(uint256 leftLeafId) internal pure returns (bool) {
        unchecked {
            return
                (iLEAVES_NUM - (leftLeafId & iLEAVES_NUM_MASK)) <= iTRIAD_SIZE;
        }
    }

    function _isEmptyTree(uint256 nextLeafId) internal pure returns (bool) {
        return (nextLeafId & iLEAVES_NUM_MASK) == 0;
    }

    function _nextLeafId2LeavesNum(
        uint256 nextLeafId // declared as `internal` to facilitate testing
    ) internal pure returns (uint256) {
        // equiv to `nextLeafId / iTRIAD_SIZE * TRIAD_SIZE + nextLeafId % iTRIAD_SIZE`
        unchecked {
            return
                (nextLeafId >> iTRIAD_SIZE_BITS) *
                TRIAD_SIZE +
                (nextLeafId & iTRIAD_SIZE_MASK);
        }
    }

    // Returns `triadIndex` index in the triad-node of the given leaf = { 0, 1, 2 }
    function _getTriadIndex(uint256 leafId) internal pure returns (uint256) {
        return getLeafIndex(leafId) % TRIAD_SIZE;
    }

    // Returns `triadNodeIndex` index of the triad-node of the given leaf
    // This index is the path to this node - used by anyone who needs the path
    function _getTriadNodeIndex(uint256 leafId)
        internal
        pure
        returns (uint256)
    {
        unchecked {
            // equiv to `leafId % LEAVES_NUM`
            uint256 iIndex = leafId & iLEAVES_NUM_MASK; // throws away tree-id bits
            uint256 fullTriadsNum = (iIndex + 1) >> iTRIAD_SIZE_BITS; // computes index of triad node in the tree
            return fullTriadsNum;
        }
    }

    // nextLeafId must be even
    function _nextLeafId2CacheIndex(uint256 nextLeafId)
        private
        pure
        returns (uint256)
    {
        // equiv to `nextLeafId % (CACHED_ROOTS_NUM * iTRIAD_SIZE) + 1`
        return (nextLeafId & CACHE_SIZE_MASK) | 1;
    }

    function _isCorrectCachedRoot(
        uint256 treeId,
        bytes32 root,
        uint256 cacheIndex
    ) private view returns (bool) {
        uint256 v = _cachedRoots[cacheIndex];
        return v == (uint256(root) ^ treeId);
    }

    // NOTE: The contract is supposed to run behind a proxy DELEGATECALLing it.
    // For compatibility on upgrades, decrease `__gap` if new variables added.
    // slither-disable-next-line unused-state
    uint256[50] private __gap;
}

