// SPDX-FileCopyrightText: Copyright 2021-25 Panther Protocol Foundation
// slither-disable-next-line solc-version
pragma solidity ^0.8.4;

/// @title ImmutableOwnable
abstract contract ImmutableOwnable {
    /// @notice The owner who has privileged rights
    address public immutable OWNER;

    /// @dev Throws if called by any account other than the {OWNER}.
    modifier onlyOwner() {
        require(OWNER == msg.sender, "ImmOwn: unauthorized");
        _;
    }

    constructor(address _owner) {
        require(_owner != address(0), "ImmOwn: zero owner address");
        OWNER = _owner;
    }
}
","keccak256":"0x06d3e09256c510bd1a5c8f6f05cc99fbc0d51919f2a64e022395bc169c349d90","license":"MIT"},"contracts/common/Types.sol":{"content":"// SPDX-License-Identifier: MIT
// SPDX-FileCopyrightText: Copyright 2021-25 Panther Protocol Foundation
// slither-disable-next-line solc-version
pragma solidity ^0.8.4;

struct G1Point {
    uint256 x;
    uint256 y;
}

// Encoding of field elements is: X[0] * z + X[1]
struct G2Point {
    uint256[2] x;
    uint256[2] y;
}

// Verification key for SNARK
struct VerifyingKey {
    G1Point alfa1;
    G2Point beta2;
    G2Point gamma2;
    G2Point delta2;
    G1Point[] ic;
}

struct SnarkProof {
    G1Point a;
    G2Point b;
    G1Point c;
}

struct PluginData {
    address contractAddress;
    bytes callData;
}

struct ElGamalCiphertext {
    G1Point c1;
    G1Point c2;
}

// For MASP V0 and V1
struct ZAsset {
    // reserved (for networkId, tokenIdPolicy. etc..)
    uint64 _unused;
    // 0x00 by default
    uint8 version;
    // Refer to Constants.sol
    uint8 status;
    // Refer to Constants.sol
    uint8 tokenType;
    // 0x00 - no scaling
    uint8 scale;
    // token contract address
    address token;
}

struct LockData {
    // Refer to Constants.sol
    uint8 tokenType;
    // Token contract address
    address token;
    // For ERC-721, ERC-1155 tokens
    uint256 tokenId;
    // The account to transfer the token from/to (on `lock`/`unlock`)
    address extAccount;
    // The token amount to transfer to/from the Vault (on `lock`/`unlock`)
    uint96 extAmount;
}

struct SaltedLockData {
    // Refer to Constants.sol
    uint8 tokenType;
    // Token contract address
    address token;
    // For ERC-721, ERC-1155 tokens
    uint256 tokenId;
    // The salt to use w/ CREATE2
    bytes32 salt;
    // The account to transfer the token from/to (on `lock`/`unlock`)
    address extAccount;
    // The token amount to transfer to/from the Vault (on `lock`/`unlock`)
    uint256 extAmount;
}
","keccak256":"0xb086580b8c0d95db80863b6775cc19ba85206cc3197e1577592872e2fee610e7","license":"MIT"},"contracts/common/UtilsLib.sol":{"content":"// SPDX-License-Identifier: MIT
// SPDX-FileCopyrightText: Copyright 2021-25 Panther Protocol Foundation
// slither-disable-next-line solc-version
pragma solidity ^0.8.16;

library UtilsLib {
    function safe24(uint256 n) internal pure returns (uint24) {
        require(n < 2 ** 24, "UNSAFE24");
        return uint24(n);
    }

    function safe32(uint256 n) internal pure returns (uint32) {
        require(n < 2 ** 32, "UNSAFE32");
        return uint32(n);
    }

    function safe40(uint256 n) internal pure returns (uint40) {
        require(n < 2 ** 40, "UNSAFE40");
        return uint40(n);
    }

    function safe64(uint256 n) internal pure returns (uint64) {
        require(n < 2 ** 64, "UNSAFE64");
        return uint64(n);
    }

    function safe96(uint256 n) internal pure returns (uint96) {
        require(n < 2 ** 96, "UNSAFE96");
        return uint96(n);
    }

    function safe128(uint256 n) internal pure returns (uint128) {
        require(n < 2 ** 128, "UNSAFE128");
        return uint128(n);
    }

    function safe160(uint256 n) internal pure returns (uint160) {
        require(n < 2 ** 160, "UNSAFE160");
        return uint160(n);
    }

    function safe32TimeNow() internal view returns (uint32) {
        uint256 t = block.timestamp;
        require(t < 2 ** 32, "UNSAFE32TIME");
        return uint32(t);
    }

    function safe32BlockNow() internal view returns (uint32) {
        uint256 b = block.number;
        require(b < 2 ** 32, "UNSAFE32BLOCK");
        return uint32(b);
    }

    function revertZeroAddress(address account) internal pure {
        require(account != address(0), "UNEXPECTED_ZERO_ADDRESS");
    }
}
