pragma solidity 0.8.16;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

contract RewardManager is Ownable {
    address public constant ZKP = 0x4004C49aBb96B11D89A52DeCCa2D1522da7f3089;

    struct User {
        address user;
        uint256 rewards;
    }

    User[] public users;

    function updateUsers(User[] memory _users) external onlyOwner {
        for (uint i = 0; i < _users.length; i++) {
            users.push(_users[i]);
        }
    }

    function getUsers() external view returns (User[] memory) {
        return users;
    }

    function distribute() external onlyOwner {
        for (uint i = 0; i < users.length; i++) {
            IERC20(ZKP).transfer(users[i].user, users[i].rewards);
        }
    }

    function withdraw(address _token, uint256 _amount) external onlyOwner {
        IERC20(_token).transfer(owner(), _amount);
    }
}
